css:
@-moz-document domain('leankitkanban.com'), domain("leankit.com") {

div.laneCapacity {
    border: 2px solid #DDDDDD !important;
  }

  #view {
    background: #296178 !important;
  }

  #user-invitation-button {
    -moz-user-select: none;
    background: #296178 !important;
  }

  #toolbar {
    background: #1f586f !important;
    background-color: #004359 repeat scroll 0 0 transparent !important;
    border-top:  !important;
    box-shadow: none !important; 
  }

  li.card .card-background {
    border: 1px solid #888 !important;
    box-shadow: none !important;
  }

  #kb-tabs, .parentLane, .childParentLane, .childLane, .laneContents {
    background: none repeat scroll 0 0 rgba(255, 255, 255, 1) !important;
  }

  .little-arrow, .little-arrow-background {
    
    display:  !important;
  }

  body {
    
    background-image: none !important; 
    
    background-color: #eee !important;
  }

  .laneName {
    
    color: #444 !important;
  }

  .laneCapacity {
    
    color: #444 !important;
  }

  .laneLaneHeader div {
    
    background-color: #ccc !important;
  }

  .parentLaneHeader div {
    
    background-color: #ccc !important;
  }

  .allLaneHeader {
    
    background-color: #fff !important;
  }

.modal-dialog {
background-color: #1f586f!important;
background-image: none !important;
background-repeat: no-repeat;
background-position: bottom left;
display: none;
padding: 5px;
width: 710px;
height: 498px;
border-top-left-radius: 10px;
border-top-right-radius: 10px;
border-bottom-right-radius: 10px;
border-bottom-left-radius: 10px;
box-shadow: rgb(51, 51, 51) 0px 10px 50px;
}

.ui-tabs-selected .border {
background-color: #fff !important;
border-bottom: 1px solid #046283;
height: 32px;
margin-top: -1px;
-webkit-border-radius: 5px;
}

.ui-tabs-panel {
float: left;
width: 486px;
height: 100%;
background-color: #fff !important;
-webkit-border-radius: 5px;
-moz-border-radius: 5px;
-ms-border-radius: 5px;
-o-border-radius: 5px;
border-radius: 5px;
-webkit-box-shadow: 1px 1px 2px #046283;
-moz-box-shadow: 1px 1px 2px #046283;
-ms-box-shadow: 1px 1px 2px #046283;
-o-box-shadow: 1px 1px 2px #046283;
box-shadow: 1px 1px 2px #046283;
font-size: 13px;
}

.save {
text-shadow: none !important;
background: #2b8eb3 !important;
}

.button {
text-shadow: none !important;
background: #2b8eb3 !important;
}

.cancel {
text-shadow: none !important;
background: #8c8887 !important;
}

.ui-tabs-selected {
padding: 7px 5px 2px 0;
height: 32px;
background-image: none !important;
background-position: right top;
background-repeat: no-repeat;
position: relative;
}

.blockOverlay {
opacity: 0.6 !important;
}

#addCard-button {
background-color: #217fa5 !important;
}

::-webkit-scrollbar-thumb {
height: 50px;
background-color: #ccc !important;
-webkit-border-radius: 5px;
box-shadow: none !important;
}


}
