css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {
body {
background: url('http://media.tumblr.com/6001fcc231afcbcdbe84ecf9cc533927/tumblr_inline_mm6w0rMGQb1qz4rgp.png') bottom left #fdfcfc no-repeat fixed !important;
}

#tinymce {background: #ffffff !important;}

.post_full .post_notes_avatar .avatar_frame {
position: relative !important;
}

.dashboard_iframe {
background: none !important;
}

.radar .radar_tumblelog_label .name, .radar .radar_tumblelog_label .subheading {
color:#0c0d13;
}

.avatar_and_i {
background:#0c0d13;
border-radius:10px;
}

.l-header {
background:#0c0d13 !important;
border-radius: 15px !important;
border: 1px outset #444;
margin-top: -8px !important;
margin-bottom: 20px !important;
padding-bottom: 0px !important;
box-shadow: 0px 10px 8px -12px #000 !important;
height: 60px !important;}

ol#posts li blockquote,
body.mceContentBody blockquote {
margin-left: 0 !important;
margin-right: 0 !important;
padding-left: 10px !important;
border-width: 4px !important;
border-color: #dadada!important;
}

ol#posts li.notification.single_notification {
background-color: #36322d;
font-size: 11px;
}

#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: transparent !important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: transparent !important;
-webkit-border-radius: 20px;
-moz-border-radius: 20px;
border-radius: 20px;
margin: auto;
padding: 40px auto;
}

h1 {
color: #989898;

}

.no_posts_found, h1.dashboard_header {
color: #989898;
}

img#content_top, img#content_bottom {
display: none;
}


#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {
background: transparent;
}

#nav .nav_item.active {
background: transparent !important;
}

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
background: transparent !important;
}

.notification  {
background:#e7e5e3 !important;
border:none !important;
font-size: 11px;
}

.notification .notification_sentence{
color:#070707 !important;
}

.notification :link, .notification :active, .notification :visited {
color:#070707 !important;
text-decoration:none !important;
border:none !important;
}

#posts .notification .hide_overflow {
color:#0c0d13 !important;
}

#posts .notification blockquote {
color:#0c0d13;
border-left: solid 3px #667B90;
}

ol#posts li.post .permalink {
background: transparent;
}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {
background: transparent ;
border: solid #000000;
}

ol#posts li.post .post_avatar{
background: #0c0d13;
}

#right_column .controls_section .with_subtitle small, .a {
color: #f5f4f0;
}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {
border-top: 1px solid #c07474;
border-bottom: 1px solid #c47c7c;
}

#right_column .controls_section {
border-top-width: 0!important;
background: #0c0d13;
}

form.dashboard_options_form {
background-color: transparent ;
border-bottom-left-radius: 10px 10px;
border-bottom-left-radius: 10px 10px;
border-bottom-right-radius: 10px 10px;
border-bottom-right-radius: 10px 10px;
border-top-left-radius: 10px 10px;
border-top-left-radius: 10px 10px;
border-top-right-radius: 10px 10px;
border-top-right-radius: 10px 10px;
color: #cccccc;
display: block;
font-size: 13px;
margin-bottom: 25px;
padding: 15px 22px;
}

form.dashboard_options_form .option_container {
border-top: 1px solid #d29b9b;
}

.no_posts_found, h1.dashboard_header {
color: #2c2c3d;
}

.answer_container {
background-color: #000000;
border-bottom: 0px solid #9d9d9d;
}

#nav .nav_item .nav_menu {
background-color: #000000;
border-bottom: 2px solid #9d9d9d;
}

#nav .nav_item .nav_menu a {
border-top: 1px solid #9d9d9d;
}

#previous_page_link, #next_page_link, .add_and_remove,
.mass_editor, .MissingE_marklist_title, .customize,
.recessed, .answer_avatar, .flat, .dashboard_options_form,
#auto_pagination_loader{
background:transparent !important;
border-top: 0px;
} 

#right_column .dashboard_nav_item a:hover {
color: #cccccc;
}

#right_column .dashboard_nav_item a {
color: #a56161;
}

#right_column .dashboard_nav_item ul.dashboard_subpages li a:hover {
color: #bb5959;
}

#right_column .dashboard_nav_item ul.dashboard_subpages li a {
color: #a56161;
}

#right_column .dashboard_nav_item .dashboard_nav_title {
color: #a56161;
}

#right_column {
color: #a56161;
}

#right_column a#dashboard_switch_blog_menu_current {
color: #f2f2f2;
}

#nav .nav_item {
color: #000000;
}

#nav .nav_item:hover {
color: #333333;
}

.new_post_notice {
background-color: #333333;
}

.new_post_notice img {
opacity: 0;
}

#search_box input {
border: 1px solid #fff;
}

#footer {
color: #ae9999;
}

#footer a {
color: #ae9999;
}

#dashboard_controls_radar_media_fold {
opacity: 0;
}

.radar .radar_controls_buttons {
background: #0c0d13;
}

#edit_post_controls #slug_url {
color: #ae9999;
}

h2 .optional {
color: #ae9999;
}

h2 {
color: #ae9999;
}

#promos .promo .tidbit {
color: #ae9999;
}

#edit_post_controls .advanced_option label {
color: #ae9999;
}

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  margin-top: 0px !important;
  border-bottom: solid 4px #0c0d13 !important;
  border-right: 11px solid transparent !important;
  border-left: 11px solid transparent !important;
}

.selection_nipple:not([class="selection_nipple white"]) {
  border-bottom: solid 11px #0c0d13 !important;}

div.return_to_top_icon{	background: none !important;
width: 0px !important;
height: 0px !important;
margin-top: 0px !important;
border-bottom: solid 35px #080806 !important;
border-right: 20px solid transparent !important;
border-left: 20px solid transparent !important;
}

.post_question {
font-family: 'Courier new' !important;
font-size: 16px !important; 
background: #36322d !important;
color: white !important;
}

.post_question:after {
background: transparent !important;
width: 0px !important;
height: 0px !important;
margin-top: 0px !important;
border-top: solid 7px #36322d !important;
border-right: 7px solid transparent !important;
border-left: 7px solid transparent !important;
}

.follower {
background: white;
}

.follower.alt {
background: #e0ecee;
}

#right_column:after{
background: transparent !important;
}

#left_column #posts .post .permalink{ background:transparent!important; }

.link_button {
min-height: auto !important;
font-size: 16px !important; 
border: 0px !important;
background: none !important;
color: #322E28 !important;
}

.link_button .link_text_container {
padding:0px !important;
}

.link_button .link_title {
color: #322E28 !important;
font-size: 16px !important; 
}

.link_button .link_source {
color: #322E28 !important;
font-size: 10px !important; 
}

.link_button .link_text_outer {
height: auto !important; 
}

.old_archive {
background: none !important;
}

#dashboard_controls a, #dashboard_controls .control, #pagination a {
  color: #0c0d13 !important;
  text-shadow: none;
}

#dashboard_controls a:hover, #pagination a:hover {
  color: #000 !important;
  text-shadow: none;
}

/* new ask */

.post_full.is_note .post_body .note_item, .main_content.note_form .note_item {
  background-color: #0c0d13 !important;
  color: #fdfcfc !important;
  font-family: 'Cambria', 'Calibri', 'Helvetica' !important;
}

.post_full.is_note .post_body .note_item a, .main_content.note_form .note_item a {
/* link askbox */
  color: #ececec !important;
}

.post_full.is_note .asker, .post_full.is_note .answerer,
.main_content.note_form .note_item .asker, .main_content.note_form .note_item .answerer {
  color: #ada8a2 !important;
  font-weight: 900 !important;
  font-style: italic !important;
  text-transform: lowercase !important;
}
              
.post_full.is_note .answerer .name, .post_full.is_note .asker .name {
  color: #ada8a2 !important;
  text-decoration: underline !important;
}

.post_full.is_note .answerer a.name:hover, .post_full.is_note .asker a.name:hover {
  color: #e9e9e9 !important;
}
              
        
.post_full.is_note .nipple, .post_full.is_note .nipple::after,
.main_content.note_form .nipple, .main_content.note_form .nipple::after {
  border-left: 8px solid #0c0d13 !important;
}

/* tumblr right column */

.controls_section li.popover_button_blogs .open_blog_link, .blog_menu .selected_blog #open_blog_link {
color: #fff;
font-size: 11px !important;
}

/* footer posts */
    
/* */.post_full .post_notes_label.note_count:hover {
color: #000 !important;
}

.chrome.blue {
background: #1F1F25 !important;
border-color: #1F1F25 !important;
}
    
#post_form .popover_menu:not(.label_view) .option:hover, #post_form .popover_post_options:not(.label_view) .option:hover {
background: #EEEEEE !important;
}
    
#post_controls button.close {
background: #000 !important;      
}

.search_form_row {
background: rgba(255, 255, 255, 0.15) !important; 
}
/* removing new tumblr bakground 17072014 */

.l-content {
background: none !important; 
}

}

@-moz-document url("https://www.tumblr.com/settings") {
body {
background: url('http://media.tumblr.com/6001fcc231afcbcdbe84ecf9cc533927/tumblr_inline_mm6w0rMGQb1qz4rgp.png') bottom left #fdfcfc no-repeat fixed !important;
}
}

@-moz-document url-prefix("http://www.tumblr.com/customize/"), url-prefix('https://www.tumblr.com/customize/')   {
body {
background: none !important;
}
}
