@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("octopart.com") 
{

body
{
    background: #161616 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAH0lEQVQI12NgYGD4LycnB8cMKioqqAJwBlQlXACmEgCsthUB9mCnuQAAAABJRU5ErkJggg==") repeat !important;
}

img#f-logo, .header-searchbar-layout-col1 a img, 
#g-header-icon, .toolbar-logo, 
a.content-results-part-datasheet img, a.content-results-part-nodatasheet img {
	height: 0 !important;
	width: 0 !important;
}

img#f-logo {
	padding-left: 300px !important;
	padding-top: 80px !important;
	background: url(https://github.com/inductiveload/stylish-bathys/blob/master/octopart/Octopart%20light.png?raw=true) no-repeat !important;
}

/* adverts, social media, etc */
#f-advert,
#f-likebutton {
    display: none !important;
}

textarea {
    background-color: #111 !important;
    border-color: #000 !important;
    color: #CCC !important;
}

.g-search-box-wrapper a {
    background-color: #111 !important;
}

/* buttons */
.jp-btn, .jp-btn {
    background: #111 !important;
    border-color: #000 !important;
    color: #BBB !important;
}

.header-searchbar-layout-col1 a img, #g-header-icon, .toolbar-logo  {
	padding-left: 181px !important;
	padding-top: 57px !important;
	background: url(https://github.com/inductiveload/stylish-bathys/blob/master/octopart/Octopart%20dark%20small.png?raw=true) no-repeat !important;
}

/* main nav bar*/
#g-navbar-bg,
.g-app-header,
#g-header-bg,
.g-app-footer {
    background-color: #111 !important;
    border-color: #000 !important;
    box-shadow: none !important;
}

#g-header-icon {
    margin-top: -15px;
}

a.content-results-part-datasheet img {
	padding-left: 16px !important;
	padding-top: 16px !important;
    background: url(https://github.com/inductiveload/stylish-bathys/blob/master/icons/pdf-16.png?raw=true) no-repeat !important;
}

a.content-results-part-nodatasheet img {
	padding-left: 16px !important;
	padding-top: 16px !important;
    background: url(https://github.com/inductiveload/stylish-bathys/blob/master/icons/pdf-grey-16.png?raw=true) no-repeat !important;
}

body, #layout-col2-inner-wrapper {
    color: #AAA !important;
}

#layout-body,
div#main-container.body-layout
{
    background: none !important;
}

.header-wrapper, #g-header-top-bg, #g-header-bottom,
.toolbar-wrapper, .content-filtersummary-wrapper,
.partsearch_sidebar, tr.shaded, .ui-tabs-panel,
#layout-col1-inner
{
    background-color: #333 !important
}

.content-filtersummary-wrapper, .partsearch_sidebar
{
    border: 1px solid black !important;
}

a.content-results-part-title, span.nv {
    color: #91DBF7 !important;
}

a,
.sidebar-filter-label.unselectable,
#offertable tbody tr,
div.title, #summary-title, 
h1, h2, h3 {
    color: #EEE !important;
}

#f-category-table td a {
    color: #AAA !important;
}

#g-header-top li
{
    text-shadow: none !important;
}

#g-header-top li a
{
    color: #aaa !important;
}

.sidebar-categories, td.nodename > a {
    color: #aaa !important;
}

.sidebar-node-line-selected-ancestor,
#g-footer-bg,
div.code-example-content, .highlight, pre,
.endpoint > li label, .content-noresults
{
    background: #222 !important
}

.sidebar-node-line-selected,
th {
    background: #111 !important;
    color: #FFF !important;
}

.sidebar-node-toggleswitch {
    color: #888 !important;
}

#banner-wrapper, #banner_wrapper {
    border-left: none !important;
}

th, #offertable td, #specs td, .schema-group td, .rest-tabdata td {
    border: 1px solid black !important;
}

td.sectionnamecol
{
    border: none !important;
}

td.offertable-pricecol,
td.offertable-stockcol,
.content-results-part-offertable,
.specs-valcol, code
 {
    color: green !important;
}

#home-logo-table {
    background: #FFF;
}

 .endpoint > li {
    box-shadow: none;
}

.endpoint-container-header-row1 td.selected {
    background: #444 !important;
}

#f-category-table th
{
    border: none !important;
}

.button, button, input[type="button"], input[type="reset"], input[type="submit"]
{
    box-shadow:none !important;
}

/* breadcrump bars*/
.jp-breadcrumb {
    background-color: #111 !important;
}

.jp-breadcrumb > li {
    text-shadow: 0 1px 0 #000 !important;
}

.jp-nav-tabs > .active > a, .jp-nav-tabs > .active > a:hover, .jp-nav-tabs > .active > a:focus {
    background-color: #111 !important;
        border-color: #000 !important;
}

/* row highlighting*/
.grandchild-row:hover {
    background-color: #111 !important;
}

/* part pages*/

#filtersummary {
    background-color: #111 !important;
    border-color: #000 !important;
}

.part-results-snippet-wrapper div {
    background: none !important;
    margin-bottom: 10px !important;
}

/* grey table rows */
.part-results .gray .col-seller, .part-results .gray .col-sku, .part-results .gray .col-stock, .part-results .gray .col-moq, .part-results .gray .col-pkg, .part-results .gray .col-curr, .part-results .gray .col-pb {
    background-color: #111 !important;
}

}
