css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.soup.io/friends"),
               url("http://www.soup.io/fof"),
               url("http://www.soup.io/stalkers"),
               url("http://www.soup.io/everyone"),
               url("https://www.soup.io/friends"),
               url("https://www.soup.io/fof"),
               url("https://www.soup.io/stalkers"),
               url("https://www.soup.io/everyone")
     {

body {
    background-color: #b5b5b5 !important;
    font-weight: normal;
    font-family: Ubuntu, Calibri, Helvetica, Arial !important;
    font-size: 16px;
    text-align: left;
}

embed {
    border-radius: 4px !important;
}

.caption {
    left: 2px !important;
    border-radius: 3px;
    margin-right: 2px;
    margin-left: -2px;
    margin-bottom: 0px;
}

#pagebody {
    margin: 0px 220px 0px 0px
}

#head .nsfw label {
    padding-top: 0px !important;
    padding-right: 18px !important;
}

#head h2 {
    font-size: 12px;
    color: #000 !important;
    text-align: left;
} 

#head {
    font-size: 11px;
    background-color: #b5b5b5 !important;
}

/*  if you want to turn on the logo, you can do it using the selector below  */

#logo h1 a {
    display: none !important;
    position: absolute;
    top: -10px;
    left: 819px;
    -moz-transform: rotate(8deg);	
    display: none;
}

.post_quote blockquote .start {
    color: #777 !important;
}

.post_quote
.body {
    font-weight: 700 !important;
}

a {
    color: #b5b5b5;
}

.actionbar, 
body.repost-iframe #repost-container {
    border-radius: 4px 4px 4px 4px;
}

.post .actionbar li.visible a,
.post .actionbar li.open a {
    background: url("http://soup.io/images/black50.png") repeat scroll 0% 0% transparent;
    border-radius: 3px;
}

div.content-container {
    padding: 7px;
    border: 1px solid #888;
    border-radius: 3px 3px 3px 3px;
    margin: 0 auto;
    background-color: #bbb;
}

/*
.post,
.reactions,
li.original_link_open,
.toggle,
.button,
.inner {
    border-top-right-radius: 3px !important;  
    border-top-left-radius: 3px !important; 
}
*/

.post .reactions .original {
    clear: both;
    padding: 0.5em;
    background: url("http://soup.io/skins/default/black30.png") repeat scroll 0px 0px transparent;
    border-bottom-right-radius: 3px !important;
    border-bottom-left-radius: 3px !important;
    border-top-right-radius: 3px !important;
}

.dropdown .dropdown_body {
    border-bottom-right-radius: 3px !important;
    border-bottom-left-radius: 3px !important;
}

div.imagecontainer {
    margin: 0 auto;
}

img {
    margin: 0px auto !important;
    max-width: 480px;
    border-radius: 3px 3px 3px 3px;
}

a.avatarlink img,
.source a.url img {
    border: 1px solid #888;
    border-radius: 1px 1px 1px 1px;
}

div.embed {
    margin-left: 0px;
}

#headercontainer2 {
    background-color: inherit;
}

textarea,
.post .content cite,
.post .content cite input {
    font-family: ubuntu;
}

#descriptioncontainer2 h4 {
    display: none;
}

#head .admin-button {
    display: none;
}

#bar ul {
    font-family: ubuntu;
    font-size: 14px;
    background-color: #333;
}

#content h2.date {
    content: ' ';
    font-family: ubuntu;
    font-size: 13px;
    font-weight: 700;
    text-transform: capitalize;
}

#avatarcontainer {
    visibility: hidden;
}

.post .author img {
    border-radius: 3px 3px 3px 3px !important;
    border: 1px solid #666 !important; 
}

#tv_promo {
    display: none !important;
}

a.tv_promo {
    display: none !important;
}

#sidebar .box {
    display: none !important;
}

#friends h3 {
    font-size: 14px !important;
    color: #ccc !important;
}

#sidebar {
    margin-top: 44px !important; 
    position: relative;
    bottom: 100px;
    left: 200px;
    max-height: 100px !important;
}

.switchable1 {
    height: 59px !important;
    color: #666 !important;
}

#main {
    margin-top: 8px;
    float: left;
    width: 555px !important;
    margin-left: 35px;
}

#friends h3,
#friends #sidebar h3 {
    clear: both;
    font-size: 1.3em;
    border-bottom: 1px solid rgb(153, 170, 187);
    width: 85%;
    text-align: left;
    float: right;
    color: rgb(51, 51, 51);
    margin: 0.4em 0px 0.1em;
    padding: 1px 4px 0.1em 0.1em !important;
    height: 15px !important;
}

h3.userlist_heading:nth-child(2) {
    border-bottom: 1px solid #b5b5b5 !important;
}

h3.userlist_heading:nth-child(4) {
    border-bottom: 1px solid #b5b5b5 !important;
}

h3.userlist_heading:nth-child(6) > a:nth-child(1) {
    display: none;
}

h3.userlist_heading:nth-child(6) {
    display: none;
}

#main ol {
    margin: 25px !important;
    padding: 0px;
}


}
