css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("soartex.net") {

/*replaces copywright info*/

#skin_copyright {
    text-indent: -9999px;
    display: block;
    width: 500px;
    height: 8px;
    background-image: url('http://i.imgur.com/VoGhcMJ.png');
    background-repeat: no-repeat;
}

/*replaces header (wood) image*/

#XenForo body header #header_wrap   {
    background-image: url('http://i.imgur.com/4DluwyM.png')!important;
}

/*replaces avatar thumbnail holders*/

.avatarHolder   {
    padding-left: 10px!important;
    padding-right: 10px!important;
    box-shadow: #CCCCCC 3px 3px 3px!important;
}
    
/*replaces navigation (cobblestone) image*/

#XenForo body header #header_wrap #navigation   {
    background: url('http://i.imgur.com/cKX3zT4.png')!important;
    opacity: 1;
}

/*replaces mundane "Quick Links" text from drop-downs*/

.muted  {
    display: none;
}

/*replaces fanver logo*/

#logo   {
    display: block;
    width: 353px;
    height: 50px!important;
    text-indent: -9999px;
    background-image: url('http://img89.imageshack.us/img89/2074/10127127xe.png');
    background-repeat: no-repeat;
    margin-top: 17px!important;
    margin-bottom: 14px!important;
}

/*replaces subheading from homepage*/

.subHeading {
    background: black!important;
    opacity: 0.6!important;
}

/*replaces online staff heading*/

.section.staffOnline.avatarList .secondaryContent h3    {
    background: black!important;
    opacity: 0.6!important;
    color: white!important;
    text-shadow: none!important;
}

/*replaces subheading links*/

.subHeading a {
    color: white!important;
    font-weight: bold;
}

/*replaces subheadings from categories page*/

.maintitle.categoryNodeInfo {
    background: black!important;
    opacity: 0.6!important;
}

/*replaces subheadings from forum categories*/

.sectionHeaders {
    background: black!important;
    opacity: 0.6!important;
    color: #FFFFFF!important;
    text-shadow: none!important;
}

/*replaces description from topic pages*/

#pageDescription.muted.baseHtml {
    font-size: 8pt!important;
    padding-left: 15px!important;
}

/*replaces login message*/

.baseHtml.noticeContent {
    display: none;
}

/*replaces top of calendar icon*/

.newsMonth.heading  {
    background: url('http://i.imgur.com/cKX3zT4.png')!important;
    opacity: 1;
    text-transform: lowercase!important;
}

/*replaces main calendar icon shape*/

.newsDate.secondaryContent  {
    background: #333333!important;
    opacity: 0.68;
    text-transform: lowercase!important;
    border-radius: 8px!important;
    color: #FFFFFF;
    box-shadow: #CCCCCC 3px 3px 3px!important;
    border: black 1px!important;
}

/*replaces media title*/

#mediaRecent.secondaryContent.mediaSmall h3 {
    background: url('http://i.imgur.com/cKX3zT4.png')!important;
    opacity: 0.8;
}

/*replaces media title link*/

#mediaRecent.secondaryContent.mediaSmall h3 a   {
    color: #FFFFFF!important;
    text-shadow: none!important;
}

/*replaces find member title*/

.secondaryContent.findMember h3 {
    background: url('http://i.imgur.com/cKX3zT4.png')!important;
    opacity: 0.8;
}

/*replaces find member title link*/

.secondaryContent.findMember h3 a   {
    color: #FFFFFF!important;
    text-shadow: none!important;
}

/*replaces posting member title*/

.secondaryContent.avatarHeap    h3  {
    background: url('http://i.imgur.com/cKX3zT4.png')!important;
    opacity: 0.8;
    color: #FFFFFF!important;
    text-shadow: none!important;
}

/*replaces chat heading*/

#taigachat_sidebar.section.taigachat_reverse .secondaryContent h3   {
    background: url('http://i.imgur.com/cKX3zT4.png')!important;
    opacity: 0.8;
}

/*replaces chat heading link*/

#taigachat_sidebar.section.taigachat_reverse .secondaryContent h3 a   {
    color: #FFFFFF!important;
    text-shadow: none;
}

/*replaces status update heading*/

.section.avatarList #statusUpdates.secondaryContent h3    {
    background: url('http://i.imgur.com/cKX3zT4.png')!important;
    opacity: 0.8;
    color: #FFFFFF!important;
    text-shadow: none!important;
}

/*replaces online members heading*/

.section.membersOnline.userList .secondaryContent h3    {
    background: url('http://i.imgur.com/cKX3zT4.png')!important;
    opacity: 0.8;
}

/*replaces online members heading link*/

.section.membersOnline.userList .secondaryContent h3 a  {
    color: #FFFFFF!important;
    text-shadow: none!important;
}

/*replaces share links heading*/

.sidebar.btmRightBlocks .section .secondaryContent h3   {
    background: url('http://i.imgur.com/cKX3zT4.png')!important;
    opacity: 0.8;
    color: #FFFFFF!important;
    text-shadow: none!important;
}

/*replaces recent news panels*/

.section.sectionMain.recentNews  {
    border-top-left-radius: 8px!important;
    border-top-right-radius: 8px!important;
}

/*replaces avatar block*/

.avatar.Av24m   {
    box-shadow: #CCCCCC 3px 3px 3px!important;
}

/*replaces title*/

h1  {
    font-size: 10pt!important;
    text-transform: uppercase;
    padding-left: 14px!important;
}

/*replaces all <h3> tags*/

h3  {
    opacity: 1;
}

/*replaces white background*/

#content    {
    background-image: url('http://i.imgur.com/4DluwyM.png')!important;
    background-repeat: repeat!important;
}

/*replaces header in download page*/

.page-header  {
    display: block;
    width: 353px;
    height: 50px!important;
    background-image: url('http://img89.imageshack.us/img89/2074/10127127xe.png');
    background-repeat: no-repeat;
}

/*replaces img and title in download page*/

html body .container .page-header h1   {
    display: none;
}

/*replaces tables in download page*/

.table.table-striped    {
    border: 1px solid #AAAAAA;
    box-shadow: 4px 4px 4px #222222;
}

/*replaces © info in download page*/

.nav.nav-pills  {
    text-indent: -9999px;
    background-image: url('http://i.imgur.com/VoGhcMJ.png')!important; 
    background-position: center;
    background-repeat: no-repeat;
}

/*replaces h3 tags in download page*/

html body .container #modded h3  {
    margin-bottom:5px!important;
}

/*replaces h4 tags in download page*/

html body .container #modded h4  {
    margin-bottom:5px;
}

}
