css:
/*
 * Style:   Bathys
 * Site:    Slashdot
 * Author:  Inductiveload
 * License: CC-BY-SA
 * File:    slashdot/slashdot.css
 */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document  domain("slashdot.org"){
}


@-moz-document  regexp("https?://(?!.*-?beta).*slashdot\.org/.*"){

body
{
  background: #161616 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAH0lEQVQI12NgYGD4LycnB8cMKioqqAJwBlQlXACmEgCsthUB9mCnuQAAAABJRU5ErkJggg==") repeat !important;
}

#content,header.h,#content,li.enhancedspacer, #d2header, .generalbody
{
    background: none repeat scroll 0 0 #111 !important;
    border: none !important;
}

.p a {
    color: #009999 !important;
}

.p a:visited, #links a:visited {
    color: #007777 !important;
}



#clickgen > .st > section, #clickgen > section > div, #clickgen > aside > div,
#slashboxes article, table.bar, footer, #linky .boxy, #linky-wrap,
#vert-related .odd, #vert-related .even
 {
    border: none !important;
}

.p, .c, .legend, aside, .by, .otherdetails, .tm, .commentBody, .details,
#poll-content, .notes, .totalVotes, .barAnswer, .firehose > span a, .btn, .bodytext
{
    color: #AAA !important;
}

.details a, blockquote.msg p, .pollBooth_view h1, .widget-wrap
{
    color: #CCC !important;
}

#firehose article > p i, article .quote, #editpreview article > p i, .commentBody .quote
{
    border-left: 3px solid #444444 !important;
}

#comments .full
{
    border: none !important;
    background: rgba(100, 100, 100, 0.15);
}

#besttabs, #newa2footerv2 .pr h3,#newa2footerv2 .ns h3,#newa2footerv2 .rl h3,
#clickgen .tlnav, .thisday-tb, #firehose-message-tray p, #firehose article.briefarticle header h2, h2.firehose,
.modal-box, footer, #vert-header-bg, .widget-wrap
{
    background: #333 !important;
}

#firehose article.briefarticle header h2{
    border-radius: 10px 10px 10px 10px;
}

div.details, #poll-content, #comments article, #firehose article, #slashboxes article,
#searchquery, blockquote.msg p, #vert-related .even
{
    background: #222 !important;
}

#fhtablist_new li.active, #fhtablist_new li.active span,
.tcenter.before a, .tcenter.after a, td.barPercent
{
    background: #111 !important;
}

.book_data
{
    background: #111 !important;
    color: #AAA !important;
}

#fhtablist_new li.active a
{
    text-shadow: none !important;
}

#dice_jobs {
    display: none !important;
}

button, input[type="submit"], input[type="reset"], input[type="button"], a.prevnextbutdis, a.prevnextbutact
{
    background: -moz-linear-gradient(center top , #222 0%, #333 100%) repeat scroll 0 0 transparent !important;
    border: 1px solid #888 !important;
    box-shadow: none !important;
    color: #AAA !important;
}

.fhitem input, .fhitem textarea, input.s, #freshmeat input{
    color: #000 !important;
}
}

/*
 * BETA Site
 */

@-moz-document  regexp("https?://.*beta\.slashdot\.org/.*"){

body, div#view-standard
{
  background: #161616 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAH0lEQVQI12NgYGD4LycnB8cMKioqqAJwBlQlXACmEgCsthUB9mCnuQAAAABJRU5ErkJggg==") repeat !important;
  color: #aaa !important;
}

p,
.comments-story,
.comment-body {
  font-size: 0.8rem !important;
  line-height: 1.2em !important;
}

.comments-story {
  font-style: italic !important;
}

.comment-title {
  color: #aaa !important;
}

h1 {
  font-size: 1.1rem !important;
}

#geojobs-nav,
.news-content,
img.hero,
.job-header, #contextualJobs {
    display: none !important;
}

article.comment-article.com-show,
.split-left,
div.menu
{
  background: #222 !important;
  color: #aaa !important;
  border-color: #111 !important;
  box-shadow: none !important;
  padding: 10px 10px 10px 20px !important;
}

#view-standard .layout-article,
#view-standard header.story-header, 
#view-classic header.story-header, 
#view-headline header.story-header
{
  padding-top: 5px !important;
  margin-bottom: 0px !important;
}

#view-standard .featured-wrap,
#view-standard .featured-wrap [class^="feature-"]
{
    height: 80px !important;
}

#view-standard .featured-wrap [class^="feature-"] img
{
    height: 200px;
}

#view-standard .featured-wrap [class^="feature-"] header.story-header {
  background-color: rgba(0, 0, 0, 0.8) !important;
}

#view-standard .layout-article .comment-count,
#view-standard .featured-wrap [class^="feature-"] header.story-header a {
  font-size: 0.8rem !important;
}

.container,
div#commentlisting,
div.col-river,
div.col-rail,
div.split-left,
div.split-right,
.container.ts, .container > .nav-wrap
{
  background: #181818 !important;
}

.container.ts {
    padding-top: 5px !important;
}

.container > .nav-wrap {
    height: 36px !important;
}

.layout-article, .layout-rail
{
  border-top: 10px solid #181818 !important;
}

.rail-module,
.geeknet-video-widget .widget-title,
h1 a, h2 a, h1, h2
{
  color: #999 !important;
}

.rail-poll li {
  padding-bottom: 0 !important;
}

.slashbox-list li {
  border-color: #000 !important;
}

.container.leaderboard,
#banner-wrap, #dice_jobs, #sourceforge2,
.layout-rail:not([id])
{
  display: none !important;
}

.well,
#layout-footer,
.mobmenu li,
.poll-list li
{
  background: #111 !important;
}

.search input
{
  color: #fff !important;
}

/* Hide the "search" text which overspills the box*/
.search .btn.fa-search
{
  color: transparent !important;
}

/* but show the search icon */
.search .fa-search:before
{
  color: #018D8E !important;
  }

input, textarea, select
{
  background: #222 !important;
  color: #ccc !important;
  border: 1px solid black !important;
}

.menu-filter, .comment-filter,
.news-content h3, .topics h3 {
    border-bottom: 2px solid #111 !important;
}

.menu-filter > li > a, .comment-filter > li > a,
.poll-list p {
    color: #999 !important;
}

.btn-vanilla {
  background: #111 !important;
}

.btn-primary {
  background: #006962 !important;
}

.btn-nutral, .btn-nav, .btn-default {
  background: #111 !important;
  border: 1px solid #000 !important;
}

h4 {
  margin-top: 0 !important;
  margin-bottom: 7px !important;
}

.comment-footer {
  padding-bottom: 5px !important;
}

a[class^="vote-"] {
  background-color: #222 !important;
  border: 1px solid #000 !important;
}

.toolbar {
    border-color: #000 !important;
}

.sd-callout
{
    background-color: #111 !important;
}

}

/* Beta story mode */

@-moz-document  regexp("https?://.*beta\.slashdot\.org/story/.*"){

.threshold-badge {
    border-color: #000 !important;
}

/*lose the right rail */
div.rail-right {
    display: none !important;
}

div.river-left {
    margin-right: 0 !important;
}

#view-standard .topic, #view-classic .topic, #view-headline .topic
{
    right: 60px !important;
}

#view-standard .layout-article .comment-count
{
    top: 10px !important;
}

.comment-body blockquote, .comment-body q, .comment-body .quote 
{
  border-color: #111 !important;
}

}
