css:
@-moz-document domain(www.nerdz.eu) {

/*
 Created and developed by Evolution, Follow The web.
 This is a css user style for modding the light nerdz css.
 
 Thanks to Nessuno, Emiliano, Deletefile, Alchimist, Robertof...
*/




/* se here for know border box http://paulirish.com/2012/box-sizing-border-box-ftw/ */
* { -moz-box-sizing: border-box  !important; -webkit-box-sizing: border-box !important; box-sizing: border-box !important; }



/* content nerdzS style */
div#postlist{
  margin: 4% 2.5% 0 2.5%  !important;
}

div.nerdz_from{
  padding: 0 30px 0 5px !important;
}
div.nerdz_message{
  padding: 6px 10px !important;
  
  font-size: 0.95em !important;
  color: #fff !important;
  
  border: 5px solid #333 !important;
  background: #222 !important;
  
  transition: all 0.5s !important;
  -webkit-transition: all 0.5s !important;
}
div.news{
padding: 6px 10px !important;
  
  font-size: 0.95em !important;
  color: #fff !important;
  
  border: 5px solid #666 !important;
  background: #444 !important;
  
  transition: all 0.5s !important;
  -webkit-transition: all 0.5s !important;
}
 div.nerdz_comments{
  padding: 6px 10px !important;
  
  font-size: 0.95em !important;
  color: #fff !important;
  
  border: 5px solid #333 !important;
  background: #444 !important;
  
  transition: all 0.5s !important;
  -webkit-transition: all 0.5s !important;
}
div.nerdz_message:hover{
  background: #111 !important;
}

div.nerdz_comments:hover{
  background: #333 !important;
}

div.qu_main{
  border: 1px solid #555 !important;
  font-style: italic !important;
}

a:hover, a:active, .qu_user a:visited, .qu_user a:active, .qu_user a:link, .atilda{
  color: #4292db !important;
}

a.showcomments, a.delpost, a.hide, div.nerdz_message .nerdz_date{
  color: #aaa !important;
}




/* aside */
aside div.title{
  background: #333 !important;
  color: #ccc !important;
  border: 0px !important;
  padding: 5px 0 5px 0 !important;
}




/* fast nerdz */
textarea{
 background: #c2dcf3 !important;
 padding: 10px 20px !important;
 transition: all 0.5s !important;
 -webkit-transition: all 0.5s !important;
}
textarea:hover{
  background: #edf4fb !important;
}

button, input[type=submit]{
  padding: 5px 10px !important; 
  background: #7bb3e6 !important;
  transition: all 0.5s !important;
  -webkit-transition: all 0.5s !important;
  margin: 0 10px 0 0 !important;
}

button:hover, input[type=submit]:hover{ 
  background: #509add !important;
}





/* header */
header#site_title{
  background: #98c3eb !important; 
}

header span#pmcounter{
 /* !important necessary to cross inline element style  */
  background: #98c3eb !important;
}


header div#title_left a{
  font-size: 1.3em !important;
  color: #222 !important;
}




/* footer */
footer#footer_main{  
  background: #98c3eb !important;
  height: 28px !important;
 }
 
form#footersearch input{
  border-radius: 0 !important;
  border: 0px !important;
  padding: 5px 10px !important;
}

footer span#bug_bar{
  height: 27px !important;
}

footer a#notifycounter{
  height: 27px !important;
  padding-top: 6px !important;
}

/* BBcode Fix */
div#center_col div.nerdz_message code div{
  font-size: 1.33em !important;
  padding-top: 6px !important;
}

/* NTI Fix */
input.ntiBtn, input.ntiHidden{
  padding: 1px 4px !important; 
  background: #333 !important;
  color: #aaa !important;
  transition: all 0.5s !important;
  -webkit-transition: all 0.5s !important;
  margin: 3px 1px 0 0 !important;
}

input.ntiBtn:hover, input.ntiHidden:hover{ 
  background: #555 !important;
  color: #ccc !important;
}

}
