css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("etsy.com") {

.post-body {font-size:12px !important ; }
.post-body {color:black !important ; }
.post-reply {background-color:rgb(245,245,241) !important ; border-bottom:1px solid rgb(132,132,132) !important ; margin:2px 0px 2px 0px !important ; }
.post-reply.inline {background-color:rgb(248,230,224) !important ; }
.post-reply.focused {padding-right:0px !important ; background-color:rgb(224,242,247) !important ; }
.post-reply.focused .post-content {padding-left:0px !important ; }
.flag a:link, .flag a:visited {color:rgb(250,88,88) !important ; }
.flag a.post-voting-trigger.active-vote {color:rgb(4,180,4) !important ; }
.taglist .tag {margin:2px 2px 0px 0px !important ; padding:2px 4px !important ; border-radius:0px !important ; }
.thread .details .title {font-size:12px !important ; font-family:"Arial","Helvetica",sans-serif !important ; font-weight:700 !important ; }
.thread .details .title a.read {color:rgb(1,146,181) !important ; }
.thread .avatar img {height:30px !important ; width:30px !important ; border-radius:2px 2px 2px 2px !important ; padding:0px 0px 0px 0px !important ; }
.thread .avatar {width:50px !important ; }
.thread .avatar .latest {height:30px !important ; width:30px !important ; border:0px !important ; }
.thread .posts .count {padding:3px 6px !important ; }
.thread td:last-child {padding:4px 0px 0px 0px !important ; }
.thread td {padding: 4px 7px !important ; }
.meta-info {font-weight:bold !important ; }
.meta-info {font-size:14px !important ; }
.meta-info a {font-size:14px !important ; }

.thread .date a {color:rgb(132,132,132) !important ; }
#content .row-heading {margin:15px auto !important ; }
#content .row .primary {padding: 15px 0px 0px !important ; }
.ff-medium {font-family:"Arial","Helvetica",sans-serif !important ; font-weight:600 !important ; }
.content-wrap {background-color:rgb(250,250,250) !important ; }

}
