@-moz-document domain("facebook.com") {


/* ta bort reklam och fixa sÃ¥ att applications alltid syns */

#ad_0, #ad_1, #ad_2, #ad_3, #ad_4, 
#ad_5, #ad_6, #ad_7, #ad_8, #ad_9,
#ssponsor,
.advert,
.ad_body,
.ad_capsule,
.ad_media,
.comments_ad_image,
.sponsors,
.sponsor_absolute { display:none !important; }

/* Remove the unnecessary New Stuff and Next Step
   sidebar items from the Facebook Home page. */
#new_stuff_content,
.nextstep { display:none !important; }
#more_section { display:none !important; }
#expandable_more { display:block !important; }
#more_link { display:none !important; }
div#ssponsor { display:none !important; }

/* ####################################################### */ 







/* gÃ¶ra toppsakerna svarta */

#sidebar a.go_home {


background:transparent url(http://img146.imageshack.us/img146/766/fondoofc7.gif) no-repeat scroll left top !important;/*FONDO NICOLE*/



}






#navigator {
background:transparent url(http://img77.imageshack.us/img77/7937/fondoochicoyp4.gif) repeat scroll 0% !important;/*FONDO EN SEE*/

}


#navigator .main_set li a.edit_link {
color:white !important;

}




#navigator .main_set li a.global_menu_arrow, #navigator .main_set li a.global_menu_arrow_active {

background:transparent url(http://img157.imageshack.us/img157/7057/flechaau6.gif) no-repeat scroll 0px center !important;/*ACA VAN LAS FLECHAS JOJO*/

}


#navigator .secondary_set li a {
color:white !important;/*COLOR INICIO I ESAS COSAS*/
}

/*
#navigator .main_set li a.active, #navigator .main_set li a:hover {
background:transparent none repeat scroll 0% !important;
}
*/


#navigator .main_set li a.active, #navigator .main_set li a:hover {
background:#408080 none repeat scroll 0% !important;/*MOUSE SOBRE */
}



h2 a {

color: #200020 !important;/*COLOR MININOTICIAS*/
}













#sidebar .divider_bar {
display: none !important;
}

#app_non_nav_list { margin-top: -10px !important; }






a { color: #808080 !important;}/* COLOR LINK*/

#navigator a {
color:white !important;
text-decoration:none !important;
}



}
