#page {
width:auto !important;
padding:0px 35px !important;
max-width:1920px;
min-width:870px !important;
font-family:"Roboto","Open Sans";
}

#page-inner {

}
#header,
#footer {
width:890px !important;
margin:0px auto 20px auto !important;
}
#main {
width:auto !important;
padding:0px !important;
background:none !important;
}
#main-inner {
margin:0px auto;
width:100% !important;
background:none !important;
}
#sidebar-right {
max-width:700px;
width:50% !important;
margin:0px 0px 0px 50% !important;
background:none !important;
}
#sidebar-right-inner {
margin:0px 0px 0px 220px !important;
background-color: #FFDD00;
border-radius:18px 18px 0px 0px;
padding-top:1px !important;
}
#sidebar-right:before,
#sidebar-right:after {
content:"";
position:absolute;
bottom:0px;
top:100px;
left:50%;
max-width:480px;
width:-moz-calc(50% - 220px);
margin:0px 0px 0px 220px;
background:#FFDD00;
z-index:-1;
}
#sidebar-right:after {
background:#0071BC;
bottom:-80px;
height:70px;
top:auto;
border-radius:0px 0px 18px 18px;
}

#content {
width:50% !important;
margin:0px -1200px 0px 220px !important;
}
#content:before,
#content:after {
content:"";
position:absolute;
bottom:0px;
top:100px;
right:50%;
margin-right:-205px;
width:650px !important;
background: url("http://www.gramajda.pl/sites/all/themes/gramajda/g/pudla.gif") 
no-repeat scroll right bottom #FFFFFF;
}
#content:after {
background:#0071BC;
bottom:-80px;
height:80px;
top:auto;
border-radius:0px 0px 18px 18px;
background: url("http://www.gramajda.pl/sites/all/themes/gramajda/g/stopa.gif") no-repeat left 0px transparent;
}
#content-inner {
float:right;
width:610px !important;
margin:0 15px 0 0 !important;
padding:0 20px 85px 20px !important;
background: url("http://www.gramajda.pl/sites/all/themes/gramajda/g/pudla.gif") 
no-repeat scroll right bottom #FFFFFF;
border-radius:18px 18px 0px 0px;
position:relative;
z-index:10;
}
#content-header,
#content-area,
#content-bottom {
margin:auto;
}
div#footer {
background:none !important;
position:relative;
}


div#sidebar-right h2 {
    background-color: #333333 !important;
    border-bottom: 1px solid black;
    border-top: 1px solid black;
    color: #FFFFFF;
    display: block !important;
    font-family: "kabel","impact";
    font-size: 16px !important;
    font-weight: normal !important;
    line-height: 32px !important;
    margin: -40px -10px 5px !important;
    padding: 0;
    text-align: center;
    text-shadow: 0 1px 5px #000000, 0 0 2px #111111;
}
#sidebar-right .block .block-inner {
    background:none !important;
    padding-bottom:0px !important;
}
.front #content-top h2 {
    font-family: "kabel","impact" !important;
    font-size:16px !important;
}
.view-comments-newmark .views-admin-links {
margin-left:-300px;
}

#block-views-comments_recent-block .more-link a {

}
#block-views-comments_recent-block .view-footer:after {
display:block;
content:"";
clear:both;
}
#block-views-comments_recent-block .view-footer > div {
float:left;
height:50px;
width:50%;
}

    @media (min-width: 1275px) {
#block-menu-menu-podreczne {
right:auto !important;
left:0px !important;
top:0px !important;
padding:0px !important;
width: -moz-calc(50% - 460px) !important;
}
#block-menu-menu-podreczne .content,
#block-menu-menu-podreczne .menu {
display:block !important;
opacity:1 !important;
}
div#sidebar-right #block-menu-menu-podreczne h2 {
position:static !important;
width:auto !important;
height:auto !important;
padding:6px 0px !important;
margin:0px !important;
border-width:1px 0px !important;
background:none !important;
text-indent:0px !important;
color:#111 !important;
text-shadow: 0 0px 2px rgba(255, 255, 255, 0.15) !important;
font-family:"kabel","impact";
font-size:16px !important;
font-weight:normal !important;
}
div#block-menu-menu-podreczne .content {
background-color: rgba(33, 40, 55, 0.0) !important;
border: 0px solid #000000 !important;
border-radius:18px 18px 0px 0px;
box-shadow:0 0 40px rgba(33, 40, 55, 0.0) inset !important;
}
div#block-menu-menu-podreczne ul {
padding-top:10px !important;
}
#block-menu-menu-podreczne a {
font-weight:600 !important;
font-size:14px !important;
font-family:"Open Sans";
line-height:30px;
}

#block-views-comments_recent-block .view-footer > div {
width:25% !important;
}
    }
    
#block-views-comments_recent-block .view-footer div > a {
-moz-box-sizing:border-box; 
padding:3px 0px 0px 1px;
width:96%;
margin:0px 2%;
border-bottom:1px solid rgba(0,0,0,0.8) !important;
line-height:18px !important;
box-shadow:inset 0px 11px 8px rgba(0,0,0,0.01);
font-size:13px !important;
text-align:center;
}
#block-views-comments_recent-block .view-footer a {
border:none !important;
display:block;
width:80px;
margin:auto !important;
line-height:11px !important;
font-size:11px !important;
font-weight:normal !important;
}
.view-comments-newmark strong {
float:left;
padding:0px 2px 0px 0px;
font-size:23px;
font-weight:normal;
margin-bottom:0px;
line-height:23px !important;
}

    @media (min-width: 1200px) {
#block-views-comments_recent-block .view-content,
#block-views-comments_recent-block .more-link,
#block-views-comments_recent-block .view-footer {

}
#block-views-dyskusje-block_1 {

clear:both;
}
#block-user-3 {

}
#block-user-1 {
clear:both;
}
    }

#block-views-ja-block_1 .views-field-field-opiekun-uid {
    bottom: 0;
    margin: 0;
    position: absolute;
    right: 0;
    top: 0;
    width: 122px;
}
#block-views-ja-block_1 .views-field-field-opiekun-uid .editablefields_clicktoedit_message {
    display:block !important;
    opacity:1 !important;
    bottom: 0;
    margin: 0;
    position: absolute;
    right: 0;
    top: 0;
    width: 122px;
    text-indent:-9999px;
    z-index:10;
}
#block-views-ja-block_1 .description {
    display:none;
}
#block-views-ja-block_1 .form-item,
#block-views-ja-block_1 .form-checkboxes {
    margin:0px !important;
}
#block-views-ja-block_1 .form-checkboxes label span {
    vertical-align:top;
    line-height:18px;
}
div[field='field_zapodziana'] {
    display:none;
}
#block-views-ja-block_1 div.editablefields-processed[field='field_opiekun']+div.editablefields[field='field_zapodziana'] {
    display:block;
    position:absolute;
    top:0px;
    width:119px;
    background:#0078DC;
    color:#fff;
    font-size:12px;
    z-index:5;
}
#block-views-ja-block_1 .views-field-field-opiekun-uid select {
    position:absolute;
    top:18px;
    left:1px;
    width:119px;
    z-index:10;
}
#block-views-ja-block_1 .views-field-field-opiekun-uid:hover+.views-field-title a.bluebutton {
    background-color: #2587FF !important;
    color: #FFFFFF !important;
    text-shadow: 0 0 1px #FFFFFF !important;
}


}
