css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("stackoverflow.com")
{

body,
.about-page .new-about-content-page .qa-block, .about-page .new-about-content-page .content-block
{
    background: #161616 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAH0lEQVQI12NgYGD4LycnB8cMKioqqAJwBlQlXACmEgCsthUB9mCnuQAAAABJRU5ErkJggg==") repeat !important;
}

body, .content-page {
    color: #AAA !important;
}

#hlogo a, .envelope-on, .envelope-off,
.vote-up-off, .vote-up-on, .vote-down-off,
.vote-down-on, .star-on, .star-off,
.comment-up-off, .comment-up-on, .comment-flag,
.edited-yes, a.cc-wiki-link, .feed-icon, .statsarrow,
.vote-accepted-off, .vote-accepted-on,
.vote-accepted-bounty, .badge-earned-check,
.delete-tag, .grippie, .expander-arrow-hide,
.expander-arrow-show, .expander-arrow-small-hide,
.expander-arrow-small-show, .anonymous-gravatar,
.badge1, .badge2, .badge3, .add-interesting-tag-on,
.add-interesting-tag-off, .fb-share, .twitter-share,
.gp-share {
    background-image: url("https://github.com/inductiveload/stylish-bathys/blob/master/stackoverflow/sprites.png?raw=true") !important;
}

.wmd-button > span {
    background-image: url("https://github.com/inductiveload/stylish-bathys/blob/master/stackoverflow/wmd-buttons.png?raw=true") !important;
}

#input, input,
.wmd-input {
    background: #111 !important;
    color: #AAA !important;
    border: 1px solid #000 !important;
}

.grippie {
    background: #000 !important;
    border-color: #000 !important;
}

#content{
    background: none !important;
}

span.feed-icon {
    background-color: transparent !important;
}

h1, h1 a, h2, .reputation-score, .page-description,
#seTabSites, .reputation a, .vote-count-post  {
    color: #EEE !important;
}

td.postcell, .comments, .module, .module p.label-key,
.subheader a,
.answer-hyperlink, .votes-cast-stats th {
    color: #AAA !important;
}

/* page number boxes etc */
.page-numbers {
   background: #222 !important;
   border: 1px solid #000 !important;
}

/* tob bar dialog - sitwswitcher etc */
.topbar-dialog {
   background: #222 !important;
   border: 1px solid #111 !important;
}

.topbar-dialog.siteSwitcher-dialog .current-site-container {
   background-color: #111 !important;
}

.topbar .icon-site-switcher-on,
.topbar-dialog .header {
   background-color: #000 !important;
}

.topbar-dialog .modal-content li {
    border-bottom: 1px solid #111 !important;
}

/* border under the tabs */
.subheader {
    border-bottom: 1px solid #333 !important;
}

.subheader #tabs .disabled {
    border: 1px solid #000 !important;
}

/* search boxes */
.topbar-dialog .site-filter-input,
.search-container input[type="text"] {
    background: #111 !important;
    border: 1px solid #333 !important;
    color: #bbb !important;
}

#hsearch {
    border: #555 1px solid !important;
}

#hsearch .textbox, .tag-editor  {
    background: #111 !important;
    color: #EEE !important;
    border-width: 0 !important;
}

div#herobox-mini {
    background: #222 !important;
    border: none !important;
}

#footer, tr.comment:hover, #tabs a:hover,
.accepted-answer {
    background: #222 !important;
}

#wmd-button-bar, div#hero-content {
    background: none !important;
}

div#herobox {
    background: #111 !important;
    border: 1px solid black !important;
}

.answer-help-background,
.review-bar-container .review-bar {
    background: #222 !important;
}

.stats {
    background: #111 !important;
}

.mdhelp, .mdhelp-tabs, .newuser,
.subtabs a.youarehere, .answer-votes,  .highlight-row,
.tagged-interesting,
.review-stats-count-current-user,
.review-stats-current-user,
.stats-mainbar .task-stat-table,
.stats-mainbar .task-stat-table td, .bounty {
    background: #111 !important;
    color: #AAA !important;
}

.stats-mainbar .task-stat-table td {
    border: 5px solid #555 !important;
}

code, pre, .owner, blockquote,
.user-about-me {
    background: #333 !important;
}

.welovestackoverflow, .question-status {
    background: #111 !important;
    color: #AAA !important;
}

.new-post-activity   {
    background: #444 !important;
}

.nav li {
    background: #111 !important;
}

.nav .youarehere, #tabs a.youarehere {
    background: #FF9900 !important;
    color: #000 !important;
}

div#hero-content {
    border: 3px solid #111 !important;
}

.preferred-login, .new-login-right .form-item {
    background: #333 !important;
    border: 1px solid #666 !important;
    box-shadow: none !important;
}

.show-votes .sidebar-linked .answer-votes,
.show-votes .sidebar-related .answer-votes, .date_brick {
    background: #111 !important;
    color: #AAA !important;
}

a.post-tag {
    background: #111 !important;
    border: 1px solid #333 !important;
    color: #00ADEE !important;
}

#tabs a {
    border: none !important;
    border: 1px solid #333 !important;
}

.user-show-new .user-rep-full .rep-down {
    color: #F00 !important;
}

/* code formatting*/
code span.pln, code span.pun {
    color: #D8D8D8 !important;
}

code span.kwd, code span.atv  {
    color: #FFFFFF !important;
}

code span.str{
    color: #FFC600 !important;
}

code span.tag {
    color: #FF0000 !important;
}

code span.lit {
    color: #7FB347 !important;
}

code span.com {
    color: #C7DD0C !important;
}

code span.typ {
    color: #D25252 !important;
}

/* hidden elements */
.module.community-bulletin {
    display: none;
}

/* text search results */
.result-highlight {
    color: #FFF !important;
}

}

@-moz-document domain("chat.stackoverflow.com")
{

#top-menu:before {
    background: #000 !important;
}

body.outside #container,
#sidebar #roomdesc {
   color: #bbb !important;
}

.subtabs a {
    border: 1px solid #444 !important;
}

/* roomcards */
.roomcard {
    background: #111 !important;
    color: #bbb !important;
    border: 1px solid #333 !important;
    box-shadow: 0 1px 3px #000 !important;
}

.mspark .mspbar {
    background: #444 !important;
}

.mspark .mspbar.now {
    background: #FF7B18 !important; 
}

/* search boxes*/
input[type="text"] {
   background: #111 !important;
   border: 1px solid #333 !important;
   color: #bbb !important;
}

/* messages in chat*/
.messages,
.ob-wikipedia, .ob-amazon, .ob-gist, .ob-message, .ob-lpadbug, .ob-manpage, .ob-blog, .ob-exception,
div.message .meta,
.monologue .timestamp
{
    background: #111 !important;
    color: #aaa !important;
}

#input-area {
    background: #222 !important;
}

.ob-wikipedia-title a, .ob-amazon-title a, .ob-lpadbug-title a, .ob-manpage-title a, .ob-blog-title a {
    color: #bbb !important;
}
code, pre, .owner, blockquote, .seWrapper .seNav, .user-about-me {
    color: #bbb !important;
    background: transparent !important;
}

div.message.reply-parent, div.message.reply-child {
    background: #000 !important;
}

ul#my-rooms > li.activity-1,
ul#my-rooms > li.activity-2 {
    color: #228222 !important;
}

ul#my-rooms > li.activity-3 {
    color: #888 !important;
}

.tag {
    background: #111 !important;
    color: #aaa !important;
    border-bottom: 1px solid #000 !important;
    border-right: 1px solid #000 !important;
}

#header-logo a img {
    display:none !important;
}

/* top dialog */
#seWrapper {
    background: #333 !important;
    box-shadow: 4px 4px 7px #000 !important;
    border: 1px solid #000 !important;
}

#seWrapper .siteInfo a {
    color: #aaa !important;
}

#seNavLinks {
    background: #111 !important;
    color: #aaa !important;
}

#seWrapper .seIntro,
#seWrapper .seNav,
#seWrapper .seFooter {
    background: #111 !important;
}

#seWrapper .itemBox {
    border-bottom: 1px solid #000 !important;
}

}
