css:
@-moz-document url-prefix("https://twitter.com/"){

body{
background: #CCCCCC !important;
}

.global-nav, .global-nav .well, .nav .active>a, .btn-tweet, .btn-tweet:hover, .btn-tweet:active, .nav .active>a, .card-soccer .cover {
	background:black !important;
}

.wrapper, .wrapper-narrow, .wrapper-permalink {
 width:100% !important;
padding: 54px 0 0 0 !important;
}

.content-main {
width: 100% !important;
}

a.media-thumbnail {
display: block !important;
}

.cards-base img, .permalink-tweet div.tweet-media img, div.tweet-media .cards-base img {
text-align: center; 
display: block !important;
margin: auto !important;
}

.profile-card.profile-header {
width: 100% !important;
}

.wrapper-profile .profile-card.profile-header .profile-header-inner {
height: 400px !important;
background-size: 100% 100% !important;
}

.profile-header-inner-overlay {
width: 100% !important;
}

.profile-header-inner-overlay {
bottom: 100px !important;
}

.profile-card.profile-header .stats {
float: none !important;
border-bottom: 1px solid #e8e8e8;
}

.profile-card.profile-header .stats a {
width:30% !important;
}

.global-nav .container {
width: 100% !important;
}

.mini-profile {
display:none;
}

.site-footer{
display:none;
}

.wtf-module.has-content {
display: none;
}

.trends{
display:none;
}

}
