    height: 0 !important;
    width: 0 !important;
    top: 0px !important;
    padding-left: 36px !important;
    padding-top: 40px !important;
    background: url('https://31.media.tumblr.com/f650e2c8116b34e961816c996d0afc49/tumblr_inline_ngfrkfQeNZ1qc0er4.png') !important;}

