css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document url-prefix("https://sakai.uri.edu/portal/tool"){

div.portletBody form#editTotalResults div.tier2 table[id='editTotalResults:questionScoreTable'].listHier tbody tr th:nth-child(2),
div.portletBody form#editTotalResults div.tier2 table[id='editTotalResults:questionScoreTable'].listHier tbody tr td.textTable:nth-child(2),
div.portletBody form#editTotalResults div.tier2 table[id='editTotalResults:questionScoreTable'].listHier tbody tr th:nth-child(3),
div.portletBody form#editTotalResults div.tier2 table[id='editTotalResults:questionScoreTable'].listHier tbody tr td.textTable:nth-child(3),
div.portletBody form#editTotalResults div.tier2 table[id='editTotalResults:questionScoreTable'].listHier tbody tr th:nth-child(4),
div.portletBody form#editTotalResults div.tier2 table[id='editTotalResults:questionScoreTable'].listHier tbody tr td.textTable:nth-child(4) {
display:none;
}
div.portletBody form#editTotalResults div.tier2 table[id='editTotalResults:questionScoreTable'].listHier tbody tr td.textTable div.longtext table tbody tr td{
display: inline;
}
div.portletBody form#editTotalResults div.tier2 table[id='editTotalResults:questionScoreTable'].listHier tbody tr td.textTable div.longtext {
width: auto;
}
html body div.portletBody form#editTotalResults div.tier2 table[id='editTotalResults:questionScoreTable'].listHier tbody tr td.textTable textarea {
width: 242px;
height: 35px;
}
div.portletBody table[id='editTotalResults:questionScoreTable'] 
p.act {
display: inline-block !important;
padding: 0.5em 0em 0em 1em !important;
margin: 0px;
}

div.portletBody table[id='editTotalResults:questionScoreTable']
td:nth-child(1).textTable{
vertical-align: middle !important;
}

div.portletBody table[id='editTotalResults:questionScoreTable']
td:nth-child(6).textTable{
background-color: #E9E9E9;
border-radius: 15px;
border: .25px solid #888888;
font-size: 16px;
}

div.portletBody form#editTotalResults div.tier2 table[id='editTotalResults:questionScoreTable'].listHier tbody tr:hover{
background: -moz-linear-gradient(center top , #FFF1C6, #FFF1E5) repeat scroll 0 0 transparent;
}
   
table[id='editTotalResults:totalScoreTable'] th:nth-child(2),
table[id='editTotalResults:totalScoreTable'] td:nth-child(2),
table[id='editTotalResults:totalScoreTable'] th:nth-child(3),
table[id='editTotalResults:totalScoreTable'] td:nth-child(3) {
display:none;
}

div.portletBody form#editStudentResults div.tier2 table tbody tr td table tbody tr td.tier2 h4.tier2 {
border-bottom: 1px dashed #505448;
margin-bottom:5px !important;
}


/* Collapse the comment section unless needed */
div.portletBody form#editStudentResults div.tier2 table tbody tr td table tbody tr td.tier2 div.tier2:before{
content:"+ Add Comment or Attachment";
font-weight:bold;
padding: 1em 0 0 1.5em;
}
div.portletBody form#editStudentResults div.tier2 table tbody tr td table tbody tr td.tier2 div.tier2 {
 height: 1.7em;
 overflow: hidden;
}
div.portletBody form#editStudentResults div.tier2 table tbody tr td table tbody tr td.tier2 div.tier2 {
 transition: all 0s  9999999s;
}
div.portletBody form#editStudentResults div.tier2 table tbody tr td table tbody tr td.tier2 div.tier2:active{
 transition: all 0s;
 height: 100%;
}

div.portletBody form#editStudentResults div.tier2 table tbody tr td table tbody tr td.tier2 div.tier2 {
background-color: #E0F0E0;
}
 
}
