css:
@-moz-document domain("feedspot.com") {
    .nav-logo {
		padding-left:50px;
	}
	#wrapper, .wrapper {
		margin: 0 auto !important;
		max-width:98% !important;
		min-width:690px !important;
	}
	#search {
		position:absolute !important;
  		left:0;
		top: 37px;
	}
	.input-search {width:247px}
	.c1outerwrap {
		margin-top: 43px;
	}
	#ufeedstaff {
		position: absolute !important;
		left:-500px !important;
	}    
	.side-footer, .opml, #user-input, #uvTab {
		display:none !important;
	}
	.ocn-data-desc {
		font-size:12px;
		line-height: 1.4;
	}
	.t_share, .t_co, .t_tw, .t_st, .t_fav, .dot {
		display:none !important;
	}
  	.t_email, .t_mu {
  		text-decoration:underline !important
    }
	.t_email:hover, .t_mu:hover {
      	text-decoration:none !important
    }
	#scroll_n {display:none; height:0; padding:0;}
	.scrollup {
		bottom:auto;
		top:7px;
		left:7px;
		z-index:3000;
		right: auto;
		font-size:0;
		height: 18px;
		color:#eee;
		padding-top:2px;
	}
	.scrollup:hover {
		background:#ccc;
		color:#ccc;
	}
	.scrollup:after {
		content: "Go up";
		color:#000;
		font-size:12px;
  
	}
	.curr_row, .curr_row_header {
		background-color:#ccc; 
	} 
	.curr_row .omsdt, .curr_row_header .omsdt {
		color:#eee;
	}
	.urna {
		width:auto;
		height:auto;
	}
	.unreadnode {
		left:0;
		right:0;
		top:0;
		bottom:0;
		background-color:#efefef;
		background-image:none !important;
		border-bottom: 1px solid #ddd;
  		width: auto;
  		height: auto;
  		opacity:0.3
	}
    .ocn-data {
		max-width:100%;
	}
    .views {
		z-index:2003
	}
    #ajaxmarkasread {
		display:block; 
		position: fixed;
		top: 0px;
		right:262px;
		height:35px;
		padding:3px 5px 0 10px;
	}
    #ajaxmarkasread img {
		position:absolute;
		left:50%;
		top:50%;
	}
    #ajaxmarkasread:hover {
		background:rgba(255, 255, 255, 0.1)
	}
        .mar {
		color:#fff;
		font-weight:bold;
	}
    .mar:hover {
		color:#fff;
	}
    .top-bar-in a:nth-of-type(2) {
        display:none !important;
    }
}
