css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("derpydeliverence.webs.com") {

/* Hides advertisements */
#fw-advertisement { display: none !important; }

/* Hides the AddThis button */
a.snap_noshots { display: none !important; }

/* Other stuff */
html, body { background: none #FFF !important; color: #333 !important; }
a { color: #36C !important; text-decoration: underline !important; }
input[type="text"] { color: #333 !important; background-color: #FFF !important; }

.fw-title a { text-decoration: none !important; }

#fw-head { height: 19px !important; margin-top: 10px !important; background-color: #FFF !important; }
#logo-div { background: transparent url("data:image/png;base64,") no-repeat top !important;
            height: 19px !important; }

#fw-blockContainer { background-color: transparent !important;
                     font-family: "DejaVu Sans", FreeSans, Verdana, sans-serif !important;
                     position: relative !important; top: -25px !important; }
                     
#fw-mainnavwrap { background-color: transparent !important; border-width: 0 !important;
                  font-weight: bold !important; position: relative; top: 40px !important; }

#fw-mainColumn { margin-top: 40px !important; }

.fw-title, .fw-title a { color: #333 !important; }

#fw-sidebar { background-color: #CCC !important; border-width: 0 !important; }

.fw-paragraph { background: none transparent !important; color: #333 !important;
                border-width: 0 !important; }

.fw-button { border-width: 0 !important; background-color: #24A !important; }
.fw-button span { color: #FFF !important; }

form[action$="search"] table { border-width: 0 !important; }

tr.header th { background-color: #FFF !important; color: #333 !important; }

.fw-odd, .fw-even { background-color: #FFF !important; color: #333 !important;
                    border-width: 0 !important; }
                    
#footer { border-width: 0 !important; }
#fw-footer { background: none #FFF !important; }
#fw-foottext { background: none transparent !important; border-width: 0 !important;
               color: #333 !important; text-align: center !important; }
a.fwftr-button { display: none !important; }

}
