css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("iichan.hk") {
html, body {
	background-color: #EEEEEE !important;
	color: #333333 !important;
	font-family: "Trebuchet MS",Trebuchet,serif !important;
}
a {
	color: #FF6600 !important;
}
a:hover {
	color: #0066FF !important;
}
.adminbar {
	clear:both !important;
	float:right !important;
	font-size: .8em !important;
	text-align: right !important;
}
.adminbar a {
	font-weight: bold !important;
}
.logo {
	clear:both !important;
	text-align: left !important;
	font-size:2em !important;
	font-weight: bold !important;
	color:#FF6600 !important;
	
	
}

.sublogo {
	color: inherit !important;
	font-size: .9em !important;
	margin: 0px 5px 8px;
	padding: 0 !important;
	text-align: left !important;
}

.theader, .passvalid, .news {
	background:#DDDDDD !important;
	text-align:center !important;
	padding:2px !important;
	color:#2266AA !important;
	clear: both !important;
	font-weight: bold !important;
	margin-bottom: .5em !important;
	border: solid 1px #CCCCCC !important;
	border-radius: 5px !important;
}
.postarea {
}
.rules {
	font-size:0.7em !important;
}
.postblock {
	background:transparent !important;
	color:#002244 !important;
	font-weight:bold !important;
}
.footer {
	text-align:center !important;
	font-size:12px !important;
	font-family:serif !important;
	margin: 2em 0 0 0 !important;
}
.dellist {
	font-weight: bold !important;
	text-align:center !important;
}
.delbuttons {
	text-align:center !important;
	padding-bottom:4px !important;
}
.managehead {
	background:#DDDDDD !important;
	color:#002244 !important;
	padding:0px !important;
}
.postlists {
	background:#FFFFFF !important;
	width:100% !important;
	padding:0px !important;
	color:#800000 !important;
}
.row1 {
	background:#DDDDDD !important;
	color:#002244 !important;
}
.row2 {
	background:#CCCCCC !important;
	color:#002244 !important;
}
.unkfunc {
	background:inherit !important;
	color:#789922 !important;
}
.reflink {
	font-size: .8em !important;
	font-weight: bold !important;
}
.filesize {
	text-decoration:none !important;
	color: #666666 !important;
	font-size: .8em !important;
}
.filetitle {
	background:inherit !important;
	font-size:1.2em !important;
	color:#002244 !important;
	font-weight:bold !important;
}
.postername {

}
.postertrip {
	color:#FF3300 !important;
}
.oldpost {
	color:#CC1105 !important;
	font-weight:bold !important;
}
.omittedposts {
	color:#666666 !important;
}
.reply {
    color: #333333 !important;
	background:#DDDDDD !important;
	border: solid 1px #CCCCCC !important;
	padding: 0 !important;
	margin: 0 !important;
	border-radius: 5px !important;
}
blockquote {
	margin: .5em .5em .5em 1em !important;
}
blockquote p {
	padding: 0 !important;
}
.reply blockquote {
	margin: .5em !important;
}
.doubledash {
	display: none !important;
 	vertical-align:top !important;
	clear:both !important;
	float:left !important;
}
.replytitle {
	font-size: 1.2em !important;
	color:#002244 !important;
	font-weight:bold !important;
}
.commentpostername {

}
.thumbnailmsg {
	font-size: .8em !important;
	color:#666666 !important;
}
hr {
	border-style: solid none none none !important;
	border-width: 1px !important;
	border-color: #BBBBBB !important;
}
table {
	border-style: none !important;
}
table td {
	border-style: none !important;
}
.nothumb {
	background-color: #FFFFFF !important;
	border-style: dotted !important;
	margin: .3em .5em !important;
}

.abbrev {
	color:#666666 !important;
}
.highlight {
	background:#EEDACB !important;
	color:#333333 !important;
	border: 2px dashed #EE6600 !important;
}


.spoiler {
	background: #CCCCCC  !important;
	color: #CCCCCC  !important;
}
.spoiler blockquote {
	background: #CCCCCC  !important;
	color: #CCCCCC  !important;
}
.spoiler:hover {
	background: #CCCCCC !important;
	color: #333333  !important;
}

.captcha {
	background:  transparent !important;
	color: #333333 !important;
}

.list {
	background:#DDDDDD !important;
	margin-top: 1em !important;
	padding-left: .5em !important;
	cursor: pointer !important;
        background: none !important;
}
.list div {
	margin-left: .5em !important;
	padding-left: .5em !important;
	border-left: solid 1px #CCCCCC !important;
}
.category, list div a, .list div a:hover {
        background: none !important;
}
.header {
	background:#DDDDDD !important;
	color:#333333 !important;
}
blockquote p {
	margin: 0 !important;
	padding: 0 !important;
}

tbody input[type="text"], table.postForm > tbody textarea {}

input:not([type="checkbox"]),textarea {
    border: 1px solid #CACACA !important;    
    font-size: 10pt !important;
    outline: medium none !important;
    padding: 2px 4px 3px !important;
    margin: 0 !important;
}

input[type="nya4"], input[type="password"], input[type="file"], textarea {
    border: 1px solid #CACACA !important;
    margin-right: 2px !important;
}

input[type="file"] { 
	border: 1px solid #CACACA !important;
	margin: 0 !important;
	padding: 0 !important;
}

td.postblock { 
	font-family: Arial, Helvetica, sans-serif !important;
	font-size: 12px !important;
	padding: 2px 5px 2px 5px !important;
	margin: 0 !important;
	font-weight: bold !important;
}

}
