css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.shroomery.org") {
    * {
        background: none !important;
        font-size: 14px;
        color: #000 !important;
        background-color: #fff !important;
    }
    body, td {
        line-height: 1.4;
    }

    
    #Data {
        border: 1px solid #ccc;
        padding: 15px;
        border-radius: 10px;
    }
    
    h1, h1 * {
        font-size: 24px !important;
    }
    h2 {
        font-size: 18px !important;
    }
    h3 {
        font-size: 16px !important;
    }
    a, a:link, a:active {
        color: #086FA1 !important;
        text-decoration: underline !important;
    }
    a:hover {
        color: #63ADD0 !important;
        text-decoration: underline !important;
    }
    #nav li {
        border: none !important;
        width: auto !important;
    }
    #nav li a {
        display: block !important;
        padding: 8px 15px !important;
        background-color: #A6B3CF !important;
        color: #000 !important;
        margin: 0 1px 1px 0 !important;
        text-decoration: none !important;
    }
    #nav li a {
        background-color: #CED6E7 !important;
    }
    #nav li ul a {
        width: 170px !important;
    }
    a.logo {
        color: #000 !important;
        font-size: 24px !important;
        text-decoration: none !important;
        text-indent: 0 !important;
        width: auto !important;
        height: auto !important;
    }
    td.navtd {
        height: auto !important;
    }
    .subjecttable table {
        margin-bottom: 10px;
    }
    .lighttable, .darktable, .cleartable, .subjecttable {
        border: 1px solid #ccc;
        padding: 10px !important;
    }
    blockquote, blockquote * {
        background-color: #BBDCFF !important;
        border-radius: 10px;
    }
    .postholder {
        border: 1px solid #ccc;  
        padding: 10px !important;
        width: auto !important;
        border-radius: 10px;
    }
    .tdheader font {
        font-size: 16px !important;
    }
}
