css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gigazine.net")
{
div#inner > div#leftcol > div.content > ul {
  position:relative;
  z-index:255 !important;
  width:160px;
  padding-left:0.2em;
  font-size:1em;
  font-weight:bolder;
}
  div#inner > div#leftcol > div.content > ul > li {
    list-style:none;
  }
  div#inner > div#leftcol > div.content > ul > li > a {
    display:inline-block;
    overflow:hidden;
    width:160px;
    height:1.6em;
    margin:0;
    padding:0.1em 0.8em;
    line-height:1.6em;
    border-left:5px solid #333;
    border-bottom:1px dotted #333;
  }
  div#inner > div#leftcol > div.content > ul > li > a:hover {
    min-width:36em;
    height:auto;
    padding:0.1em 0.8em;
    background:#464663 !important;
    border-bottom:1px dotted #fff;
    line-height:1.6em;
    color:#eef !important;
  }
}
