css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lxr.free-electrons.com") 
{

body
{
    background: #161616 url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAQAAAAECAYAAACp8Z5+AAAAH0lEQVQI12NgYGD4LycnB8cMKioqqAJwBlQlXACmEgCsthUB9mCnuQAAAABJRU5ErkJggg==") repeat !important;
    color: #AAA;
}

#header {
    background: #112 url("https://upload.wikimedia.org/wikipedia/commons/thumb/0/0a/Tux-shaded.svg/100px-Tux-shaded.svg.png") no-repeat !important;
    background-position:20px 15px !important; 
}

#topbar, #menu {
    background: #333 !important;
}

#wrapper {
    background: #222 !important;
}

pre, #lxrcode a {
    background: #111 !important;
}

a {
    border-bottom: 1px dotted #555 !important;
    color: #AAA !important;
}

#lxrcode a {
    color: #CCC !important;
}

#menu a {
    border-bottom: none !important;
}

img[alt="Folder"], img[alt="File"], img[alt="C file"], img[alt="Back"] {
    width: 0 !important;
    height: 0 !important;
	padding-left: 22px !important;
	padding-top: 22px !important;
}

img[alt="Folder"] {
	background: url("https://github.com/inductiveload/stylish-bathys/blob/master/icons/folder-22.png?raw=true") no-repeat !important;
}

img[alt="File"] {
	background: url("https://github.com/inductiveload/stylish-bathys/blob/master/icons/text-x-generic-22.png?raw=true") no-repeat !important;
}

img[alt="C file"] {
	background: url("https://github.com/inductiveload/stylish-bathys/blob/master/icons/text-x-c-22.png?raw=true") no-repeat !important;
}

img[alt="Back"] {
	background: url("https://github.com/inductiveload/stylish-bathys/blob/master/icons/arrow-back-22.png?raw=true") no-repeat !important;
}

h1, h2, h3 {
    color: #CCC !important;
}

}
