css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fimfiction.net") {

/* Make input boxes brighter */
input[type="text"], input[type="password"] {
    background-color: #FFF !important;
    padding: 4px !important;
    color: #000 !important;
}

input.username, input.password { padding-left: 26px !important; }

/* Hide the magnifying glass in the search bar. */
input.search {
    background-image: none !important;
    padding-left: 3px !important;
}

/* Hide the pin in the toolbar. */
.light .right .link_container:nth-child(4) {
    display: none !important;
}

/* Hide title source link. */
#source_link { display: none !important; }

/* Stretch the second toolbar. */
.header { width: 100% !important; }

/* Remove some background images. */
body { background-image: none !important; }
.body_container { background-image: none !important; }

/* Hide featured stories on the front page. */
.featured_box { display: none; }

/* Shorten title text. */
a.story_name { display: inline !important; }
.content_box_header h2 { line-height: 40px !important; }
.content_box_header .right {
    height: 40px !important;
    line-height: 40px !important;
    padding-right: 0 !important;
}

/* Search result margin fix. */
#inner { margin-left: 10px !important; }

/* Hide story statistics. */
.info { display: none !important; }
.inner_data { display: none; }
.rating_container .bars_container, .rating_container hr,
.comment_container {
    display: none !important;
}
.rating_container { border-width: 0 !important; }

/* Hide avatars. */
img.story_avatar { display: none !important; }
.author .name { position: relative; top: 8px; }
.avatar { display: none !important; }

/* Disable text shadows. */
.list_boxes ul.list li { text-shadow: 0 0 !important; }
a.story_link { color: #649240 !important; }
.list_boxes ul.list li span { color: #666 !important; }
.left, .right { text-shadow: 0 0 !important; }

/* Expand hidden chapters. */
.chapter_expander { display: none !important; }
.chapter_container_hidden { display: block !important; }

/* Reduce chapter list featurism. */
.word_count { visibility: hidden; }
.download_container { visibility: visible; }
.chapter_container li span.date { display: none; }

/* Disable hover animation. */
div.list_boxes ul.list li div.before { transition-property: none !important; }

/* Hide the right column on the front page. */
.front_page_right_column { display: none; }
.column_latest_stories { margin-right: 0 !important; }

/* Get rid of Facebook. */
.facebook_like_box { display: none !important; }

/* Hide advertisements. */
.ad_container { display: none; }
.likfhlskdhslikdf { display: none; }

/* Change story summary font. */
.padding p, ul.chapters {
    font-family: "Liberation Serif", FreeSans, Verdana, sans-serif !important;
    font-size: 12pt !important;
}

/* Hide group join dates. */
#story_group_list .date { display: none; }

/* Hide right column on story pages. */
.user_blog_post .right .content_box { display: none !important; }
.user_blog_post .left { margin-right: 0 !important; }
.story_content_box { margin: 0 auto 15px !important; }
a.expand_right { display: none !important; }
.user-links { display: none !important; }

/* Reduce comment featurism. */
a.refresh_comments { display: none !important; }
a[href^="#new_com"] { border-radius: 4px !important; }
.author .name {
    border-radius: 0 !important;
    border-width: 0 !important;
    text-shadow: 0 0 !important;
}
.author .name a {
    color: #4D8023 !important;
    border-bottom: 1px dotted #333 !important;
}
.comment_like_container { display: none !important; }

/* Hide infocard */
#infocard { display: none !important; }

/* Hide footer. */
.content_background { padding-bottom: 0 !important; }
.footer { display: none; }

}
