/*Juick dark-grey*/

/*@namespace url(http://www.w3.org/1999/xhtml);*/
@-moz-document domain("juick.com"){
    input, textarea {-moz-appearance:none !important;}
    input::-webkit-outer-spin-button, input::-webkit-inner-spin-button, input::-o-inner-spin-button, input::-o-outer-spin-button, input[type="file"]::-webkit-file-upload-button {margin:0; -webkit-appearance:none !important;}
    x::-webkit-file-upload-button, input[type="file"]:after {margin:0; -webkit-appearance:button !important;}

    :focus {-moz-outline:none; outline:none !important;}
    button::-moz-focus-inner, input[type="reset"]::-moz-focus-inner, input[type="button"]::-moz-focus-inner, input[type="submit"]::-moz-focus-inner, input[type="file"] > input[type="button"]::-moz-focus-inner {border:none !important;}
    button::focus-inner, input[type="reset"]::focus-inner, input[type="button"]::focus-inner, input[type="submit"]::focus-inner, input[type="file"] > input[type="button"]::focus-inner {border:none !important;}

    /* custom scrollbar fix*/
    /*html { width: 100% !important; margin: 0 10px !important; overflow-x: hidden; }*/
    /*body > header, body > aside, body > #footer { margin-left: -10px !important; }*/
    /*body > #content { margin-left: 276px !important; }*/
    /* End of custom scrollbar fix*/
    
    body, header p {color:#acacac !important;}
    #logo, div[id*="yandex_ad"] {background:none !important;}
    #header, #hlinks .button ul, #pagetabs li, #content.pagetext, #fwrapper, #headdiv, header p, #ctoolbar {background:#333 !important; border:none !important; box-shadow:none !important;}
    html, #header .text, .inp, #dialogl, #dialogn, .msg-comment textarea, #hlinks .button, #hlinks .button ul, strong, .msg-comment .reply, .msg-comment input, textarea, .newmessage, input[type="text"], input[type="submit"], input[type="file"], input[type="file"]:after, input[type="file"] > input[type="button"], .dialogl, .signinput, .signsubmit, article input {background:#303030 !important; color:#dadada !important;}
    .msg-comment .reply, .msg-comment input, .inp, strong, #search input.text, textarea, .newmessage, input[type="text"], input[type="submit"], input[type="file"], input[type="file"]:after, input[type="file"] > input[type="button"], .dialogshare, .dialogl, .dialoglogin, .signinput, .signsubmit, .msg-comments, #mtoolbar, .msg-tags > a, article .tags > a, fieldset, article input {border:1px solid #404040 !important;}
    .msg-comment .narrow {resize: both !important;}
    #content article, .msg-cont, .ads, .page, #mtoolbar, .title2, #dialogw, hr, #column .inp, #search input.text, #newmessage, .dialogshare, .dialoglogin {background:#333 !important;}
    a, article > nav.s a, .msg-comment input, input[type="submit"], input[type="file"], input[type="file"]:after, input[type="file"] > input[type="button"] {color:#00aef0 !important;}
    .msgthread, .msg-comments, #mtoolbar {border-color:#303030 !important;}
    .msg-comments {color:#aaa !important;}
    #footer {color:#999 !important;}
    #dialogn {cursor:text !important;}
    .ta-wrapper, article > nav.l a {border:none !important;}
    blockquote, q {border-left-color: #909090 !important;}
    q {color: #909090 !important;}
