css:
@-moz-document domain("forum.r2games.com") {
/*
   CoS forums enhancer userstyle
   Version 0.6
*/

/*
----------------------------------------
................ QUOTES ................
----------------------------------------
*/
.bbcode_quote, .bbcode_code
{
  background: #423023 none !important;
  border: 1px solid #2f2218 !important;
}

.bbcode_quote .quote_container
{
  color: #876a4a !important;
}

.bbcode_quote_container
{
  background-image: url("data:image/png;base64,") !important;
}

.bbcode_postedby img { visibility: hidden; }

.bbcode_postedby a
{
  width: 18px !important;
  height: 11px !important;
  background: url("data:image/png;base64,") no-repeat bottom;
}

.bbcode_postedby
{
  background: url("data:image/png;base64,") no-repeat top left;
}

/*
----------------------------------------
............. REPLY FIELDS .............
----------------------------------------
*/
.qrcontainer, .formcontrols, .vbform .blockfoot, .formcontrols .blockfoot, .formcontrols .blocksubfoot, .formcontrols .blockbody, .formcontrols .blockrow, .vbform .blocksubhead, .vbform .blockrow, .vbform .signature, .pollresultsblock, .postpreview, .postpreview .blockfoot, .postpreview .blocksubfoot, .postpreview .blockbody, .postpreview .blockrow, .postpreview+.blockfoot
{
  background: #302319 none !important;
}

.pollresultsblock h2
{
  color: #FF7140 !important;
}

.cke_skin_kama
{
  border: 1px solid #423023 !important;
}

.cke_skin_kama .cke_wrapper
{
  background: #2f2218 none !important;
}

.cke_skin_kama textarea, .formcontrols #title, .formcontrols #subject, .formcontrols .blockrow .textbox
{
  background: #75553e !important;
  color: #201711 !important;
  border-color: #75553e !important;
}

.cke_toolgroup, .cke_openbutton .cke_icon, .cke_font a, .cke_fontSize a
{
  background-image: url("data:image/png;base64,") !important;
}

.cke_button .cke_on, .cke_button a:hover, .cke_off a:hover .cke_openbutton, .cke_off a:focus .cke_openbutton, .cke_off a:active .cke_openbutton, .cke_on a .cke_openbutton
{
  background-color: #75553e !important;
}

.cke_font a, .cke_fontSize a
{
  border-bottom: 1px solid #75553e !important;
  border-right: 1px solid #75553e !important;
}

.cke_font .cke_inline_label, .cke_fontSize .cke_inline_label
{
  color: #876a4a !important;
}

.formcontrols, #forumrules, .vbform .blocksubhead, .vbform .blockrow, .vbform .signature, .formcontrols .blockrow .description, .pollresultsblock, .pollresultsblock h2 span, .pollresultsblock .stats, .pollresultsblock .stats dt, .pollresultsblock .stats span, .postpreview
{
  color: #C69C6D !important;
}

#forumrules
{
  border-left: 1px solid #75553e !important;
  border-bottom: 1px solid #75553e !important;
  border-right: 1px solid #75553e !important;
}

.postbit, .formcontrols fieldset
{
  border-top: 1px solid #75553e !important;
}

.postbit .header
{
  margin-bottom: 15px;
  text-decoration: underline;
  font-weight: bold;
}

.postbit .header:after
{
  content: " posted:";
}

.formcontrols legend
{
  color: #d3b490 !important;
}

/*
----------------------------------------
............. VARIOUS STUFF ............
----------------------------------------
*/
.highlight { background: #423023 none !important; } 

.signature
{
  border-top: 1px solid #C69C6D;
}
}
