css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reader.mx") {

/*首页文章列表*/
a.jPostItem { font-size:18px !important; }
/*正文标题*/
h1{font-family: 方正宋黑简体,黑体 !important; }

/*正文*/
P{ 
  text-indent: 2em !important;
  font-size: 26px !important; 
  line-height: 1.5em !important;
  font-family: 文泉驿微米黑,黑体 !important; 
}

/*正文列表*/
li p,li { 
  text-indent:0 !important;
  font-size: 22px !important; 
  line-height: 1.4em !important;
  font-family: verdana,黑体 !important;
  margin-bottom:8px !important;
}
ol,blockquote p {margin-left:6%;}

/*文中重点强调部分*/
b,strong{color:#A14242;font-family:"方正宋黑简体","仿宋" !important;}

}
