css:
@-moz-document domain("antyweb.pl") {
/*Top*/
#cover-menu, #cover-menu-shadow {height: 66px !important;}
#cover-menu .menu {height: 32px !important;}
#cover-menu .menu li.menu-item {height: 20px !important; margin-top: 10px !important;}
#cover-menu .menu li.menu-item > a {padding: 0 10px !important;}
#cover-menu .dzyndzel {height: 16px !important; top: 15px !important;}
.submenu:not(.submenu-author) {top: 30px !important;}
#cover-menu #search {height: 22px !important; margin: 3px 20px 5px 0 !important;}
/*Główna*/
.entry .entry-desc {border-bottom-left-radius: 10px !important;}
.entry .entry-desc h3 {font-size: 1.425em !important;}
.entry .entry-desc p {
 color: #363636 !important;
 font-size: 0.91em !important;
 line-height: 1.25em !important;
 padding-top: 3px !important;
}
#entries > .entry-wide .thumbnail {
  background-position: 50% 30% !important;
  height: 240px !important;
  margin-left: 14px !important;
}
/*Arty*/
#single-entry .entry-title {font-size: 1.85em !important; padding: 14px 0 !important;}
#single-entry > .entry-cover {
 position: relative !important;
 height: 200px !important;
 padding: 0 !important;
 border: none !important;
}
#single-entry > .entry-cover img {
 position: absolute !important;
 clip: rect(180px, auto, 380px, 14px)  !important;
 top: -180px !important;
}
#entry-cover-big {position: relative !important; height: 280px !important;}
#entry-cover-big img {
 position: absolute !important;
 clip: rect(100px, auto, 300px, auto) !important;
 top: -100px !important;
}
#entry-cover-big .mark {
 right: -85px !important;
 top: -85px !important;
 transform: scale(0.5) !important;
 z-index: 1 !important;
}
#single-entry .news, #single-entry .entry-cover {margin-left: 0 !important;}
#single-entry .news {margin: 0 300px 0 14px !important;}
#single-entry .entry-share {margin-left: -67px !important;}
#single-entry .entry-title-cover .left-spacer {height: 112px !important;}
.news .news-content .quote {font-size: 0.91em !important;}
.news .news-content blockquote {font-size: 0.65em !important;}
.news .news-content iframe[src^="http://www.youtube.com/"] {height: 451px !important;}

body #upprev_box a.upprev_thumbnail img {
 max-height: 180px !important;
 max-width: 350px !important;
}
.entry:not(.entry-wide) .thumbnail {width: 326px !important; left: 14px !important;}
.tags-items .entry .thumbnail {width: 234px !important;}
/*Forum*/
#forum-header > .entry {margin: -8px 0 0 !important;}
#forum-header > .entry-wide .thumbnail {
 background-position: 50% 25% !important;
 height: 200px !important;
 margin-left: 14px !important;
}
.entry-wide .entry-desc h3 {font-size: 1.84em !important;}
.forum-table .new-entry-form .ico {
 left: -7px !important;
 top: 20px !important;
}
.forum-table .forum-categories .show-more-categories {
 right: -12px !important;
 top: 0 !important;
}
#cookie-law-info-bar, .mask {display: none !important;}
}
