}

#right_column .controls_section li a,

#right_column .controls_section li a .count {

  color: #ccffcc !important; 
}

#logo, #logo a {
  position: relative;
  z-index: 1;
  width: 270px !important;
  height: 71px !important;
  background-repeat: no-repeat;
  background: url("http://img833.imageshack.us/img833/1281/007df.png")no-repeat !important;
}

