css:
@-moz-document url-prefix("chrome://stylish/content/edit.xul") {

	#stylish {
		padding: 0 0 3px !important}
	#stylish > vbox > grid > rows > row:last-child {
		padding-top: 2px !important}
	#stylish > vbox > grid {
		margin: 0 !important;
		padding: 4px 174px 4px 0 !important;
		z-index: 1 !important;
		overflow: hidden !important}
	#stylish > vbox > grid label {
		margin: 0 3px 0 10px !important}
	#stylish > vbox > grid label,
	#stylish #editor-tools .checkbox-label {
		text-shadow: 0 1px 0 rgba(255,255,255,.4) !important}
	#stylish:-moz-window-inactive > vbox > grid label,
	#stylish:-moz-window-inactive #editor-tools .checkbox-label {
		color: #575757 !important}
	#stylish > vbox > grid > rows > row {
		padding: 0 !important}
	#stylish > vbox > grid > rows > row > textbox {
		margin: 0 5px 1px !important}

	#editor-tools {
		-moz-appearance: none !important;
		margin: -29px 0 0 !important;
		padding: 0 1px 2px 0 !important;
		border: 0 !important;
		border-bottom: 1px solid rgba(0,0,0,.2) !important;
		display: -moz-box !important;
		min-height: 30px !important;
		max-height: 30px !important;
		z-index: 0 !important;
		-moz-box-align: center !important;
		-moz-box-pack: end !important;
		-moz-box-flex: 0 !important}
	#editor-tools > * {
		padding: 0 !important;
		margin: 0 4px 0 !important}

	#internal-code {
		margin: 0 !important;
		background: white !important;
		border: 0 solid !important;
		border-bottom-width: 1px !important;
		border-top-width: 1px !important;
		-moz-border-top-colors: rgb(153,153,153) !important;
		-moz-border-bottom-colors: rgb(180,180,180) !important;
		-moz-appearance: none !important;
		box-shadow: none !important;
		border-radius: 0 !important;
		font-size: 13px !important}

	#errors {
		margin: 0 !important;
		padding: 0 1px !important;
		max-height: 12em !important;
		min-height: 3em !important;
		overflow: auto !important;
		box-shadow: inset 0 1px 0 0 rgba(0,0,0,.3),
					inset 0 -1px 0 0 rgba(0,0,0,.3) !important;
		background-color: #FBB !important;
		color: #000 !important}
	#errors > label {
		margin: 0 !important;
		color: inherit !important}
	#errors[style="display: none;"] {
		display: none !important}

}
