#masterSocialbuttonTop, #ITT, #ITSB, div[id^="newzia_"],
#masterFooter, #ITRN, #ITOT, #ITADS, #ISALC, .colBoxSubcolJobboard, .colBoxArtTopRcm,
.colBox.colBoxArtTop, #my_social_top
{ display: none !important; }

body {
  background-color: #f5f5f5;
}

#masterBody, #globalHeaderMiddle {
  background-color: transparent;
}

