  width: 689px !important;
  margin: 0 auto 5em !important;
}

#masterBodyInner {
  width: auto;
}

#globalContents {
  width: auto;
}

#masterContents {
  width: auto;
  padding: 40px 0;
  background-color: #fff;
  border: none !important;
  box-shadow: 0 1px 3px #ddd;
  border-radius: 2px;
}
#masterContents, #masterMain {
  border: none !important;
}
#masterMain {
  width: auto;
  float: none;
}
