css:
/* i really want this to be global */

::-webkit-scrollbar
{
    width: 3px !important;
    height:3px !important; 
    -webkit-box-shadow: none !important;
    box-shadow: none !important;
}

::-webkit-scrollbar-thumb
{
    border-width: 0 !important;
}

.b-resizer .b-resizer__i
{
  background: #7F7F7F;
  box-shadow: none;
  left: -1px;
  right: 3px;
}

/* спиннер */
.b-mail-icon_ajax-loader
{
    height: 0;
    width: 0;
    overflow: hidden;
}
.b-mail-icon_ajax-loader:after
{
    height: 16px;
    width: 16px;
    background-size: 16px;
    background-image: url('data:image/gif;base64,');
}


.b-stamp__refresh
{
    height: 100%;
    width: 100%;
    top: 0;
    left: 0;
    padding: 12px 0 0 156px;
    box-sizing: border-box;
}
.b-header .b-stamp,
.b-header .b-stamp:before
{
    background: rgb(246, 246, 246) !important;
}
.b-header .b-stamp:hover,
.b-header .b-stamp:hover:before
{
    background: rgb(242, 242, 242) !important;
}


/* список папок */
body .b-folders .b-folders__folder_current
{
    background: none !important;
}
.b-folders__folder:hover
{
    background-color: rgba(0, 0, 0, .04) !important;
}
.b-folders__folder_current
{
    box-shadow: inset 0 2px 3px -1px rgba(0, 0, 0, .7) !important;
}

.b-layout__left .b-folders__folder_current .b-folders__folder__info,
.b-layout__left .b-folders__folder_current .b-folders__folder__name
{
    text-shadow: none;
    color: #fff;
    font-weight: bold;
    background-color: rgba(0, 0, 0, .5) !important;
}
.b-folders__folder__info:hover {
    background: rgba(0,0,0,.2);
}
.m-layout_3pane .b-statusline
{
    background: rgba(47, 142, 240, .8);
}



/* список писем */
.b-messages__message_checked,
.b-messages__message_focus,
.b-messages__message_thread-open.b-messages__message_focus + .b-messages__thread
{
    background-color: rgba(0, 0, 0, .5) !important;
    color: #777 !important;
}

.b-messages__message_important
{
    box-shadow: none !important;
}
.b-messages__message_important,
.b-messages__message_important + .b-messages__thread
{
    background-color: #3d87cc !important;

}
.b-messages__message_important:hover
{
    background-color: #4390d7 !important;
}
.b-messages__message_important .b-messages__subject,
.b-messages__message_important .b-messages__from,
.b-messages__message_important b-messages__date,
.b-messages__message_focus .b-messages__subject,
.b-messages__message_focus .b-messages__from,
.b-messages__message_focus b-messages__date
{
    color: rgba(255, 255, 255, .9) !important;
}

.b-messages__message_checked,
.b-messages__message_checked.b-messages__message_important,
.b-messages__message_focus,
.b-messages__message_focus.b-messages__message_important

{
    box-shadow: inset 0 2px 3px -1px rgba(0, 0, 0, .5),
                inset 0 -2px 3px -1px rgba(0, 0, 0, .2) !important;
}
.b-messages__message_focus + .b-messages__message_focus,
.b-messages__message_checked + .b-messages__message_checked
{
    box-shadow: none !important;
}

.b-messages__message_thread-open,
.b-messages__message_thread-open + .b-messages__thread
{
    background-color: rgba(0,0,0,.07);
}
.b-messages__message_thread-open
{
    box-shadow: inset 0 2px 2px -1px rgba(0, 0, 0, .2) !important;
}
.b-messages__message_thread-open + .b-messages__thread
{
    box-shadow: inset 0 2px 2px -1px rgba(0, 0, 0, .2),
                inset 0 -2px 2px -1px rgba(0, 0, 0, .2) !important;
}

.b-messages__icon__image
{
    box-shadow: 0 0 0 1px rgba(0,0,0,.5);
    
}

.m-layout_3pane-vertical .b-messages .b-messages__message .b-label
{
    position: absolute;
    left: 0;
    top: 0;
    height: 100%;
    width: 3px;
    color: rgba(0, 0, 0, 0);
    overflow: hidden;
    margin: 0;
    padding: 0;
}
.m-layout_3pane-vertical .b-messages .b-messages__message .b-label_rounded
{
    border-radius: 0;
}

html.m-layout_3pane-vertical .b-messages__tcount,
html.m-layout_3pane-vertical .b-messages__message_focus .b-messages__tcount,
html.m-layout_3pane-vertical .b-messages__message_thread-open + .b-messages__thread .b-messages__tcount
{
    background: none !important;
    border-color: transparent !important;
    border-radius: 0 !important;
}
.b-ico_unimportant,
.b-ico_important
{
    height: 23px;
    top: 50%;
}
html.m-layout_3pane-vertical .b-messages__tcount:hover,
html.m-layout_3pane-vertical .b-messages__message_focus .b-messages__tcount:hover,
html.m-layout_3pane-vertical .b-messages__message_thread-open + .b-messages__thread .b-messages__tcount:hover,
html.m-layout_3pane-vertical .b-ico_unimportant:hover,
html.m-layout_3pane-vertical .b-ico_important:hover,
html.m-layout_3pane-vertical .b-messages__message_thread-open .b-messages__tcount
{
    background-color: rgba(0,0,0,.05) !important;
}
.b-messages__tcount .b-messages__tcount__count,
.b-messages__tcount .b-messages__tcount__arrow_close
{
    color: rgba(0,0,0, .3) !important;
}
html.m-layout_3pane-vertical .b-messages__tcount .b-messages__tcount__count:hover
html.m-layout_3pane-vertical .b-messages__tcount .b-messages__tcount__arrow_close:hover
{
    color: rgba(0,0,0, .5) !important;
}
  
.block-messages-box.b-layout__second-pane
{
    background: rgba(255,255,255,.6);
}

.b-message-subject
{
    padding: 13px 55px 14px 33px !important;
    background: rgba(0,0,0,.05);
}

.b-message-head-wrap
{
    padding-top: 10px;
    background: rgba(255, 255, 255, .5);
}

  
  
    .b-message-subject .b-ico_important,
    .b-message-subject .b-ico_importance,
    .b-message-subject .b-ico_unimportant
    {
      position: absolute;
      height: 100%;
      width: 100%;
      left: 0;
      top: 0;
      margin-left: 0;
      background-image: none !important;
      
    }
    .b-ico_importance:hover
    {
      background-color: rgba(67, 144, 215, .05) !important;  
    }


    .b-message-subject .b-link_print
    {
      position: absolute;
      top: 0 !important;
      right: 0;
      height: 48px;
      padding: 0 20px;
    }
    .b-message-subject .b-link_print:hover
    {
      background-color: rgba(0,0,0,.05);
    }
    .b-message-subject .b-link_print > img
    {
      margin-top: 16px;
    }

    .b-message-toolbar__i
    {
      padding-right: 0;  
    }


    .b-quote__ctrls
    {
      margin: 25px 0;
    }
    .b-quote__button,
    .b-quote__expand-all
    {
      background: none;
      font-size: 10px;
      margin-left: 0 !important;
      padding: .3em 1em .3em !important;
    }
    .b-quote__button:hover,
    .b-quote__expand-all:hover
    {
      background: rgba(0, 0, 0, .05) !important;
    }

    .b-mail-button_light-selected
    {
      background: rgba(0, 0, 0, .05) !important;
    }



.b-message_collapsed .b-message-head__field__title
{
    -webkit-transition: opacity .3s ease-out;
    -webkit-transform: translateZ(0);
}


.b-layout__inner .b-message
{
    background: transparent;
    -webkit-box-shadow: none;
    box-shadow: none;
    margin: 0;
    margin-left: 0;
    margin-right: 0;
    padding-top: 0;
}


.m-layout_3pane-vertical .b-layout__second-pane .b-messages__icon__image
{
    margin: 4px;  
}
