css:
@-moz-document url-prefix("https://www.atmarkit.co.jp/ait/articles/") {
#COIDP, #masterNavigation, #globalSearch, #ITN, #globalTagline, #localNavigation,
#globalHeaderBottom, #headerBgArea, #masterSub, #mediaFooter, #masterFooter, #qa_float, #ITOT1,
.masterSocialbutton, .colBox, .btmlist, #ITSB, #ITWL, #cse-search-form, #___gcse_0,
#colBoxSideButtons, #adHeaderText, #ISALC
{ display: none !important; }

body {
  background-image: none !important;
  background-color: #f5f5f5 !important;
}
#masterBodyOut {
  width: 800px !important;
  margin: 20px auto 50px !important;
  border: 1px solid #eee;
}
#masterMain {
  float: none !important;
  width: auto !important;
  padding: 15px 35px !important;
}
#cmsTitle h1 big {
  font-size: 26px !important;
}
.logoArea {
  margin: 0 5px 10px !important;
}
#globalLogo {
  margin: 0 30px;
}
#globalContents {
  border: 0;
}
#cmsBody h3, #cmsBody h4 {
  margin: 40px 0 20px !important;
}
#tmplNews h3 {
  color: #389839;
}
#cmsBody .box h3 {
  margin: 0 !important;
}
#cmsBody .box {
  margin: 0 0 0 1em !important;
  border: 1px solid #ddd !important;
  background: #f8f8f8 !important;
  line-height: 1.3;
}
#cmsBody .box > .box {
  border: 0 !important;
}
#cmsBody strong {
  font-family: monospace;
  font-weight: bold;
  background-color: #f8f8f8;
  padding: 2px 4px;
}
.cmsBox {
  font-family: monospace, Osaka;
}
.trBoxC {
  margin-bottom: 20px;
  font-family: monospace, Osaka;
  background-color: #f5f5f5;
}
.trBoxC .trBoxWidth {
  width: auto !important;
  padding: 5px 10px;
}
.trBoxC p {
  margin-bottom: 0 !important;
}
#cmsBody .cmsTable75 {
  width: 85%;
}
#cmsBody .cmsBodyBoxInner {
  font-family: monospace;
}
}

@-moz-document url-prefix("https://www.atmarkit.co.jp/fwin2k/") {
#gnavi, #search, #header_ad, #navibar, #headmenu-area, #rightcol, #ITSB, #footer, #eyeDiv, .newziaBox, .btmlist, #btmRss
  { display: none !important; }

  #centercol {
    width: 800px !important;
    padding: 5px 20px 10px !important;
  }
  #main {
    background: #fff !important;
  }
  html {
    background: #f2f2f2;
  }
  #main #centercol p {
    line-height: 26px;
  }
  code, code * {
    font-size: 16px;
    font-family: monospace !important;
  }
}

@-moz-document url-prefix("https://www.atmarkit.co.jp/fdb/"), url-prefix("https://www.atmarkit.co.jp/fjava/"), url-prefix("https://www.atmarkit.co.jp/flinux/") {
#gnavi, #gnaviSub, #search, #header_ad, #localNav, #navibar,
#rightcol, #headmenu-area, table[width="450"], #newzia_connect_atmarkit,
#ITSB, #ISALC, .newziaBox, .btmlist, #footer
{ display: none !important; }

#header { height: 100px; }
#centercol {
  width: 760px !important;
  padding: 0 40px 20px;
}
#main {
  background-image: none !important;
  background-color: #fff;
  margin: 0 auto 4em;
  border: 0;
  box-shadow: 0 1px 3px #ddd;
}
table[height="60"]{
  border-spacing: 0;
}
tr[bgcolor="#666666"] td[colspan="2"] {
  padding: 6px 10px 4px;
  border-radius: 3px 3px 0 0;
}
tr[bgcolor="#666666"] font {
  color: #eee;
}
tr[bgcolor="#666666"] font b {
  font-weight: normal;
}
td[bgcolor="#DDFFFF"]{
  background-color: #2B2C26;
  color: #bbb;
  padding: 5px 10px 7px;
  border-radius: 0 0 3px 3px;
}
.kwout {
  margin: 2em auto;
}
body {
  color: #444;
}
h2 {
  margin: 4em 0 1em;
}
font, p {
  line-height: 1.4;
}
code, code *, .gist * {
  font-family: monospace !important;
}
}

@-moz-document url-prefix("https://www.atmarkit.co.jp/fnetwork/"), url-prefix("https://www.atmarkit.co.jp/fdotnet/") {
#gnavi, #gnaviSub, #search, #header_ad, #localNav, #navibar,
#rightcol, #headmenu-area, table[width="450"], #newzia_connect_atmarkit,
#ITSB, #ISALC, .newziaBox, .btmlist, #footer
{ display: none !important; }

#header {
  height: 100px;
}
#centercol {
  width: auto !important;
  padding: 42px 40px 20px !important;
}
#main {
  background-image: none !important;
  background-color: #fff;
  margin: 0 auto 4em;
  border: 0;
  box-shadow: 0 1px 3px #ddd;
}
font {
  line-height: 1.4;
}
p {
  color: #333;
}
code, code * {
  font-family: monospace !important;
}
table[width="500"] {
  width: 100%;
}
tr[bgcolor="#003399"] {
  background-color: #005999;
}
table[width="400"] {
  width: 600px;
}
table[width="480"], table[width="490"] {
  width: 750px;
}
table[width="480"] tr[bgcolor="#000000"] {
  background-color: #2B2C26;
}
font[color="#0066FF"] {
  line-height: 3;
}
font[color="#0066FF"] br {
  display: none;
}
table[width="392"][bgcolor="#e7e3e7"] {
  width: auto;
  padding: 1em 2em;
}
table[width="392"][bgcolor="#e7e3e7"] tr td {
  padding: 6px 0;
}
}
