css:
#pinnedContent {
position: absolute;
overflow: hidden;
background: rgb(255, 255, 255);
top: 0;
left: 140px;
min-width: 800px;
right: 0;
bottom: 0;
-webkit-box-shadow: 0 0 5px rgba(51,51,51,0.4);
-moz-box-shadow: 0 0 5px rgba(51,51,51,0.4);
box-shadow: 0 0 5px rgba(51,51,51,0.4);
-webkit-transition: right 200ms ease-out;
-moz-transition: right 200ms ease-out;
-o-transition: right 200ms ease-out;
transition: right 200ms ease-out;
background: #222222 !important;
}

#sideNavigation {
min-height: 700px;
height: 100%;
width: 140px;
background: #222222;
position: fixed;
top: 0px;
left: 0px;
z-index: 100;
}

#threadListHolder {
height: 100%;
max-width: 330px;
background-color: #222222 !important;
float: left;
overflow-y: scroll;
}


#singleMessageContainer .contentPanelHeader {
background-color: #177BBD !important;
border-bottom: 1px solid #33B5E5;
}

#singleMessageContainer .contentPanelHeaderText {
background-color: #177BBD;
color: white;
font-size: 23px;
margin-left: 18px;
margin-top: 10px;
width: 260px;
max-width: 260px;
overflow: hidden;
white-space: nowrap;
text-overflow: ellipsis;
height: auto;
line-height: 32px;
left: 55px;
}

.threadItem {
margin: 10px 0 5px;
clear: both;
box-shadow: inset 0 0px rgba(255,255,255,0.6) !important;
border-radius: 5px;
padding: 4px 15px;
max-width: 150px;
font-size: 13px;
line-height: 15px;
}

#singleMessageContainer .threadItem {
background-color: #177BBD;
padding-top: 5px;
font-weight: 400;
font-size: 14px;
line-height: 17px;
max-width: 240px;
}

.receivedText {
position: relative;
background: #177BBD !important;
border: 0px solid #555555 !important;
float: left;
text-align: left;
margin-left: 20px;
}

.sentText {
position: relative;
background: #177BBD !important;
border: 0px solid #555555 !important;
float: right;
text-align: right;
box-shadow: inset 0 0px rgba(255,255,255,0.6) !important;
margin-right: 20px;
}

#singleMessageContainer .messageThread {
position: relative;
height: 100%;
width: 500px;
background-color: #555555 !important;
margin-top: 0px;
margin-left: 0px;
float: left;
border-left: 0px solid rgb(0, 0, 0) !important;
-webkit-box-sizing: border-box;
-moz-box-sizing: border-box;
box-sizing: border-box;
-webkit-box-shadow: 0 0 5px rgba(51,51,51,0.4);
-moz-box-shadow: 0 0 5px rgba(51,51,51,0.4);
box-shadow: 0 0 5px rgba(51,51,51,0.4);
}

#singleMessageContainer .responseArea {
background-color: #555555;
height: 90px;
width: 100%;
position: absolute;
bottom: 0;
padding: 0;
}

#singleMessageContainer .threadConversationHolder {
border-bottom: 1px solid #33B5E5;
background-color: #555555;
width: inherit;
height: auto;
position: absolute;
bottom: 100px;
padding-bottom: 23px;
}

.timestamp-msg {
font-size: 10px;
color: #FFFFFF !important;
margin-right: 5px;
margin-left: 5px;
}

.threadItem > span {
color: white !important;
word-break: break-word;
}

#singleMessageContainer .charsLeft {
font-size: 14px;
position: static;
color: white !important;
}

#singleMessageContainer .sendButton {
border: 0px solid #555555 !important;
height: 40px;
width: 40px;
margin-bottom: 10px;
padding: 4px;
}

.loadMoreMessages {
color: #f9f9f9 !important;
width: 95%;
background-color: #33B5E5 !important;
text-align: center;
font-weight: bold;
margin-top: 4%;
margin-left: 2%;
padding-top: 2%;
padding-bottom: 2%;
margin-bottom: 3%;
}

#navBarTabs > .active > a, #navBarTabs > .active > a:hover {
background-color: #177BBD !important;
color: rgb(240,240,240);
}

#navBarTabs .thread-tab {
font-size: 15px !important;
font-weight: 500;
font-family: Helvetica !important;
color: rgb(77, 77, 77);
cursor: pointer;

border-bottom: 0px solid #ccc !important;
}

#navBarTabs {
background-color: #555555 !important;
margin-bottom: 5px;
border-bottom: 0;
margin-left: 0;
list-style: none;
border-right: 0px solid #ccc !important;
}

.threadNameOrNumber {
color:white;
margin-left: 5px;
display: inline-block;
width: 140px !important;
overflow: hidden;
text-overflow: ellipsis;
white-space: nowrap;
line-height: 22px;
font-family:helvetica !important;
}

#threadListHolder {
width: 300px !important;
height: 100%;
max-width: 500px;
background-color: #222222;
float: left;
overflow-y: scroll;
}

.msgCountAndTimeStampInTab {
float: right;
text-align: right;
margin-top: 6px;
font-size: 11px;
color: rgb(255, 255, 255) !important;
opacity: 0.8;
font-family: 'proxima-nova','Helvetica Neue', Helvetica, Arial, sans-serif;
}

.contactRow {
color: white;
background-color: #555555;
border-bottom: 1px solid gainsboro;
padding-top: 15px;
padding-bottom: 15px;
padding-left: 5px;
}

.contactName {
color: white !important;
display: inline-block;
font-size: 15px !important;
font-family: helvetica;
font-weight: 600;
width: 30%;
}

b, strong {
font-weight: bold;
color: white;
}

.threadConversationHolder {
width: 100%;
height: 250px;
overflow: hidden;
overflow-y: auto;
background-color: #555555;
}

.contentPanelHeader {
width: 100%;
overflow-x: hidden;
background-color: #177BBD !important;
position: relative;
z-index: 4;
}
.contentPanelHeaderText {
font-family: 'proxima-nova-condensed','Helvetica Neue', Helvetica, Arial, sans-serif,"franklin-gothic-urw-cond";
font-weight: 500;
float: left;
color: white;
font-size: 19px;
line-height: 22px;
margin-left: 8px;
margin-top: 7px;
width: 125px;
max-width: 125px;
overflow: hidden;
white-space: nowrap;
text-overflow: ellipsis;
background: #177BBD !important;
position: absolute;
left: 35px;
z-index: 5;
opacity: 1;
-moz-transition: opacity 0.1s;
-o-transition: opacity 0.1s;
-webkit-transition: opacity 0.1s;
transition: opacity 0.1s;
}
