css:
/* breadcrumb */

.content_wrap .separator .separator-view-title .breadcrumb,
.right_wrap .separator .separator-view-title .breadcrumb{
    text-align: left;
    padding: 0 0 0 170px;
}

/* task list */

.task_list_container{
  	margin: 0;
}

.task_list_container,
.task_list,
.task_list_closed,
.tasks_content .task,
#task_lists.kanban .textilized,
#task_lists.kanban .new_task .new_comment_wrap{
  	max-width: 100%;
}

#task_lists.kanban .new_task .new_comment_wrap{
  	width: 100%;
}

.tasks_content .buttons{
	margin: 8px 10px;
}

#task_lists.kanban{
	padding: 0 0 0 10px;
}

div.tasks{
	padding: 4px 0 0;
}

#task_lists.kanban .task_list,
#task_lists.kanban .task_list_closed{
  	background: rgba(255, 255, 255, 0.3);
  	border: none;
	box-shadow: 0px 0px 5px 0px #666;
  	-moz-box-shadow: 0px 0px 5px 0px #666;
	-webkit-box-shadow: 0px 0px 5px 0px #666;
}

#task_lists.kanban .button-icon{
  	margin: 4px 4px 0 0;
}

#task_lists.kanban .task_list,
#task_lists.kanban .task_list_closed{
  	min-width: 500px;
}

.resolved_switch{
  	top: 7px;
}

/* nav */

.nav_links .el.selected, .nav_links .el.selected:hover{
  	background-color: #999;
}

.nav_links .el.selected a, .nav_links .el.selected:hover a{
  	text-shadow: none;
}

/* nav of note list */

.three_pane_item.active{
  	background-color: #aaa;
  	color: #fff;
	box-shadow: inset 0 0 10px rgba(0, 0, 0, 0.3);
  	-moz-box-shadow: inset 0 0 10px rgba(0, 0, 0, 0.3);
	-webkit-box-shadow: inset 0 0 10px rgba(0, 0, 0, 0.3);
}

.page_list .three_pane_item.active h3 a{
  	color: #fff;
}

/* background and wallpaper */

.content_scroll{
    background: none #6bb !important;
    background-size: cover !important;
}

/* scrollbar */

::-webkit-scrollbar{
  	width: 10px;
}

::-webkit-scrollbar:horizontal{
  	height: 10px;
}

::-webkit-scrollbar-track{
    border-radius: 10px;
    -moz-border-radius: 10px;
    -webkit-border-radius: 10px;
	box-shadow: inset 0 0 8px rgba(0, 0, 0, .6);
  	-moz-box-shadow: inset 0 0 8px rgba(0, 0, 0, .6);
	-webkit-box-shadow: inset 0 0 8px rgba(0, 0, 0, .6);
}

::-webkit-scrollbar-thumb{
    border-radius: 10px;
    -moz-border-radius: 10px;
    -webkit-border-radius: 10px;
	box-shadow: inset 0 0 8px rgba(66, 66, 66, .4);
  	-moz-box-shadow: inset 0 0 8px rgba(0, 0, 0, .4);
    -webkit-box-shadow: inset 0 0 8px rgba(0, 0, 0, .4);
}
