css:
/* i really want this to be global */
#im_rcemoji_cont
{
  width: 160px;
}


body .add_media_item
{
  display: inline-block !important;
  width: 20px;
  padding: 3px;
  background: none no-repeat;
}


body .add_media_rows
{
  margin-left: 4px;
}


body .add_media_menu nobr,
.add_media_head
{
  display: none !important;
}


.add_media_lnk
{
  position: relative;
  display: inline-block;
  width: 100px;
  height: 18px;
  color: transparent;
}
.add_media_lnk:before
{
  display: block;
  height: 20px;
  width: 20px;
  content: '';
  font-size: 20px;
  position: absolute;
  left: 50%;
  margin-left: -7px;
  margin-top: -5px;
  background: url('data:image/png;base64,') no-repeat center;
  background-size: 50%;
  
  -webkit-transform: rotate(45deg);
}


.add_media_more_node
{
  display: inline-block !important;
}


.add_media_more_wrap
{
  position: relative;
  height: 100%;
  width: 100%;
}
