css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("members.iracing.com")

{

/*get rid of divider bar between series*/
.series-divider {display:none!important}

/*always show Qualy/TT Bar*/
.series-focus-info {height: 31px!important}

/*Make everything widescreen*/
#maincontent {width: 1900px!important; background: none!important; table-layout:auto!important;}
#pan2_col2 {margin:auto;display:block;} /*not on guide*/
#columns {margin:auto!important; width:932px!important;}
#home-segments {display: none!important}
div[id="race_grid"] {width: 1739px!important}
#testingpanel {left: 972px!important}
#racenavbar {width: 1942px!important;z-index:4!important;}
#ticker_data {width: 1747px!important}
#subnav {width: 1942px!important}
body {width:auto!important}
/*body {background: grey!important}*/
#toolbar-top {width: 1900px!important}
#toolbar-inner {width: 1900px!important}
#full_width_content {width: 1900px!important}
.ui_tab_content {width: 1900px!important}
.race-meta h3 {margin-right: 10px!important;float:right!important;}
.race-actions { right:auto!important;}

body > table:nth-child(7) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(2) > div:nth-child(1) {width: auto!important}  
#ticker_data > embed:nth-child(1)  {width: 1747px!important} 
body > table:nth-child(7) > tbody:nth-child(1) > tr:nth-child(5) > td:nth-child(2){z-index: 3!important}
#ticker_headlines {width: 1944px!important; background-repeat: repeat!important}
body > table:nth-child(7) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2){width: 1942px!important}
body > table:nth-child(7) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) > div:nth-child(1) > div:nth-child(2) {left: 1661px!important}
body > table:nth-child(7) > tbody:nth-child(1) > tr:nth-child(9) > td:nth-child(2) > div:nth-child(1){width: auto!important;background-repeat:repeat!important;}
body > table:nth-child(7) > tbody:nth-child(1) > tr:nth-child(8) > td:nth-child(2) > div:nth-child(1) {width: auto!important; border-bottom: 0px!important;}
body > table:nth-child(7) > tbody:nth-child(1) > tr:nth-child(8) > td:nth-child(2) {background: none!important}
}

@-moz-document 
url("http://members.iracing.com/membersite/member/Home.do"), 
url("http://members.iracing.com/membersite/member/Home.do?page=guide"), 
url("http://members.iracing.com/membersite/member/LeagueDirectory.do") 
{

body > table:nth-child(7) {width: auto!important}
#pan2_col2 {margin:0px!important;display:auto!important;}
}
