@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("verpets.com") {
 div#footer {
	display: none !important}

 body, div#top, div#banner, div#content, div#rightbar, div#leftbar, div#leftcol, center, td, td#td1 b, div#date, table#table1 td center font, table#table1 td font {
        background-color: #000 !important;
        background-image: none !important;
        color: #FFF !important}
 a {
        color: #0A0 !important}
 table#table1, div#banner table {
        background-color: #000 !important;
        border: none !important}
 input, select, textarea {
        background-color: #0A0 !important;
        border-color: #000 !important}
 table#table1 tr td {
        vertical-align: top !important}
 td#td1 {
       background-image: none !important;
       border: none !important}
 td#td1 b {
        font-size: 18px !important}
 hr {
        height: 1px !important}

 div#ad {
       top: 21px !important;
       left: 10px !important;
       position: absolute !important;
       background-color: #000 !important}
 div#rightbar {
       top: 100px !important;
       left: 3px !important;
       position: absolute !important}
 div#banner {
       top: -80px !important;
       left: 0px !important;
       position: absolute !important}
 div#content {
       top: 225px !important;
       left: 160px !important;
       position: absolute !important}
 div#userinfo {
       top: 110px !important;
       left: 160px !important;
       position: absolute !important}
}
@-moz-document url-prefix("http://verpets.com/forum"), url-prefix("http://www.verpets.com/forum") {
 table#table1 td center div, table#table1 td div {
        background-color: #060 !important;
        background-image: none !important;
        color: #FFF !important}
}
