@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("online-sweepstakes.com") {
 div#footer, div#cfooter, div#time, img[src="http://www.online-sweepstakes.com/static/images/online-sweepstakes.jpg"], img[src="/static/images/base/top_logout.gif"], div#homecol3, img[src="/images/editor/separator.gif"] {
	display: none !important;}

 span.sweepnum, a.serc, span.sexp, span.sdate {
	display: none !important;}

 div#sweep, div.sweepdiv, div#maindiv, body, div#header, td.alt1Active, td.alt1, td.panelsurround, td.vBulletin_editor, div.alt1, div.alt2, div#content_container, div#blog_user_sidebar {
	background-color: #000 !important;
	background-image: none !important;
	border: none !important;
	color: #FFF !important;}

 input.search {
	background-color: #000 !important;
	background-image: none !important;
	color: #FFF !important;}

 table.sprize, div#tabarea, div#tabdiv, div#tabdiv a, div#tabdiv a span, span.snewnotes, td.thead, td.tcat, td.tfoot, td.vbmenu_control, div.panel, textarea, legend, ul.thead, .tborder, h4.tcat, div.alt#blogusermenu, div.thead,  th.thead, th.tcat {
	background-color: #060 !important;
	background-image: none !important;
	border: none !important;
	color: #DDD !important;}

 select, input, td table {
	background-color: #060 !important;
	background-image: none !important;
	color: #DDD !important;}

 div.olshighlight, td table tr td {
	background-color: #040 !important;
	background-image: none !important;
	color: #AAA !important;}

 p.ssubline, span.sfreq, span.smisc, div.panel td, div.bigusername, b, font {
	color: #DDD !important;}

 div#listings div {
	color: #222 !important;}

 div.navbar#navtop form, span.mstotal, td.alt1 span, td.alt1 strong, td.alt1 div {
	color: #FFF !important;}

 td.alt1, td.alt1 li {
	background-color: #000 !important;
	background-image: none !important;
	color: #FFF !important;}

 td.alt2 {
	background-color: #333 !important;
	background-image: none !important;
	color: #DDD !important;}

 div.mbox {
	background-color: #060 !important;
	background-image: none !important;
	border: none !important;}

 a, span.selig, p.scmmts, div.smallfont, span.smallfont {
	color: #0B0 !important;}

 a:hover, a:visited {
	color: #0F0 !important;} }

@-moz-document url-prefix("http://www.forums.online-sweepstakes.com/showthread"), url-prefix("http://forums.online-sweepstakes.com/showthread") {
 div#maindiv div {
	background-color: #000 !important;
	background-image: none !important;}
}
