css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.tagesschau.de") {

html, body, div, span, applet, 
object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, 
acronym, address, big, cite, code, del, dfn, font, img, ins, kbd, q, s, 
samp, small, strike, sub, sup, tt, var, dl, dt, dd, ol, ul, li, 
fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, 
th, td {
font-family: Tahoma,Verdana,Arial !important;
font-size: 100% !important;
}

body {
background: #4169E1 !important;
}

.box {
background: #eeeeee !important;
border-top: 4px solid #4169E1 !important;
}

#header .logo {
height: 20px !important;
display: none !important;
}

#header .searchForm {
height: 24px !important;
margin-top: 0px !important;
}

.subnavi {
height: 24px !important;
width: 85% !important;
margin-top: -26px !important;
}

.sectionC .con {
margin-top: 0px !important;
}

.box.active, .box:hover, .box.active a, .box:hover a {
background-color: #dddddd !important;
color: #001A4B !important;
}

.icon {
height:36px;
}

a:hover {
text-decoration: underline !important;
}
}
