css:
@-moz-document url-prefix("http://www.reddit.com/r/leagueoflegends"), url-prefix("http://dd.reddit.com/r/leagueoflegends") {

.side .md h1, .side .md h3 {
    margin: 8px 0 0 !important;
    padding: 6px 0 !important;
    
    background-image: url("http://b.thumbs.redditmedia.com/olUBv4NFsyf5qU1m.png");
    
    border-left: 2px solid #0D0F0E;
    border-right: 2px solid #0D0F0E;
    border-top-left-radius: 6px;
    border-top-right-radius: 6px;
    
    color: #BAFFF7 !important;
    text-shadow: 0 0 1px #BAFFF7 !important;
    font-weight: normal !important;
    letter-spacing: 1px !important;
}

.side .md h1 {
    border-bottom-left-radius: 6px;
    border-bottom-right-radius: 6px;
}

.side .md blockquote {
    border-style: none solid solid !important;
    border-width: 1px !important;
    
    border-bottom-left-radius: 6px;
    border-bottom-right-radius: 6px;
    border-top-left-radius: 0px !important;
}

.side .md h1 + blockquote {
    display: none;
}

.side .md h1:hover {
    border-bottom-left-radius: 0;
    border-bottom-right-radius: 0;
}

.side .md h1:hover + blockquote, .side .md h1 + blockquote:hover {
    display: inherit;
}

.side .md h4, .side .md h5, .side .md h6 {
    margin-left: 0px !important;
    
    border-style: solid solid none;
    border-width: 1px;
    border-top-left-radius: 6px;
    border-top-right-radius: 6px;
    
    text-align: center;
    font-size: 14px !important;
}

.side .md h4 {
    background-color: #F9F4F4;
    
    border-color: #FF8888;
}

.side .md h5 {
    background-color: #EFF4F2;
    
    border-color: #44EE88;
}

.side .md h6 {
    background-color: #F4F2EF;

    border-color: #FFBB44;
}

/* Elimination of bullet points */

.side .md h1 + blockquote ul {
    list-style-type: none !important;
    margin-left: 8px !important;
    margin-top: 0 !important;
}

.side .md h1 + blockquote ul li + li {
    border-top: 1px solid #CCCCCC;
    color: #008688;
    margin-bottom: 2px;
}

}
