@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("neopets.com") {
 .adBox2, .ad_wrapper, .ad_wrapper_fixed, .ban, #ban, #ban_bottom, div.ban, div#ban, #ban_spacer, .yahoo_cmss, .marqueeSlot, hr, #footer, .footer, #footerNifty, .footerNifty, .footOverlay, #footOverlay, div#pet_rating, div.phpGamesTowerAd {
	display: none !important;} }

/* NeoQuest II */
@-moz-document url-prefix("http://neopets.com/games/nq2/"), url-prefix("http://www.neopets.com/games/nq2/") {
 div.contentModule phpGamesNonPortalView, div.frame {
	background-color: #FFF !important;
	width: 150% !important;}
 div.frame div {
	background-color: #FFF !important;
	width: 154% !important;}
 div.contentModuleHeader, img[src="http://images.neopets.com/nq2/x/donothing.gif"], img[src="http://images.neopets.com/nq2/x/1s.gif"], img[src="http://images.neopets.com/nq2/x/3s.gif"], img[src="http://images.neopets.com/nq2/x/5s.gif"] {
	display: none !important;
	background-image: none !important;} }

/* User Profiles */
@-moz-document url-prefix("http://neopets.com/userlookup"), url-prefix("http://www.neopets.com/userlookup") {
 div#usercollections, div#usercollections table.contentModuleTable, div#userneohome, div#usertrophies {
	display: none !important;}
 div#userinfo table.contentModuleTable {
	width: 765px !important;}
 div#userneopets table.contentModuleTable {
	width: 975px !important;} }

/* Stock Market */
@-moz-document url-prefix("http://neopets.com/stockmarket"), url-prefix("http://www.neopets.com/stockmarket") {
 marquee {
	display: none !important;} }
