/* eliminate vertical scrollbar */
html, body{
height: 100% !important;
overflow: hidden !important;
}


#gbzw, /* top menu container*/
#gbz /* top menu */
{
width: 100% !important;
position: relative !important;
}

/*top container - shrink for #mngb hover -> switch language */
#gb {height: auto !important;}

#gbz /* top menu */
{background: /*#2d2d2d*/ rgba(25,25,20,.5) !important;}

/* autohide */
#gbzw #gbz,  /* top menu */
#gbx3,  /* top menu bg */
#gbd, /* "more" dropdown background */
#gbs,  /* "more" dropdown background */
#gbu #gbvg, /* upper right */
.jsb /* original search buttons */
{visibility: hidden !important;} 

/* header and upper right */
#mngb:hover #gbz, /* top menu */
body #pocs.sft ~ #mngb  #gbz,
#gbu:hover  #gbvg, /* upper right */
body #pocs.sft ~ #mngb #gbvg,
body #pocs.sft ~ #mngb #gbvg #gbd5,
#gbu:hover  #gbd5, /* search settings */
#gbzw:hover #gbd[style*="visible"], /* "more" dropdown */
.jsb input:first-child/* original Search button */
{visibility: visible !important;}

/* upper right bg */
/*#gbu {
padding-bottom: 25px !important;}*/
#gb #gbu:hover{
background: rgba(25,25,20,.5) !important;}

/* "more" services dropdown */
#gbmmb
{
background: rgba(25,25,20,1) !important;
}
#gbd
{
background: rgba(25,25,20,0) !important;
border-color: transparent !important;
}

#gbztms /* "more" services button */
{
background: rgba(25,25,20,1) !important;
border-color: transparent !important;
}
a.gbmt, 
#gbztms #gbztms1 /* "more" services button */,
#gbd a.gbmt /* "more" services dropdown buttons */
{ 
color: #bbb !important;
background: rgba(10,10,8,.0) !important;
}
#gbd a.gbmt:hover, 
#gbztms:hover #gbztms1
{ 
color: #fff !important;
}
#gbztms #gbztms1 + span /* "more" services button triangle */
{
border-top-color: #bbb !important;
}

#gbg1, #gbg3 /* share,notifications */ 
{ opacity: .5 !important; }
#gbg1:hover, #gbg3:hover /* share,notifications */ 
{ opacity: 1 !important; }
#gbg6 * /* name */ 
{ color: #ccc !important; }
#gbg6:hover * /* name */ 
{ color: #fff !important; }


/* footer */
#footer{
position: fixed !important;
bottom: 0px !important;
left: 0px !important;
right: 0px !important;
}
#footer a 
{color: #ccc !important;}
#footer > div > div{
visibility: hidden !important;} /* autohide */
#footer > div:hover > div{
visibility: visible !important;}
/* bg */
/*#footer > div:hover 
{background: rgba(20,20,10,0) !important;}*/

#footer > div 
{border-color: transparent !important;}

/* auto-suggest */
table.gssb_c{
background: rgba(10,10,5,.75) !important;
}
table.gssb_c *{
background: rgba(0,0,0,0) !important;
color: rgba(250,250,200,.8) !important;
}
table.gssb_c b{
color: rgba(250,250,200,1) !important;
font-weight: normal !important;
}
.gssb_e{
border-color: rgba(128,128,128,1) !important;
}

/*html .gssb_m > tbody > tr:not(:last-child):hover*/
html .gssb_m > tbody > tr:hover td.gssb_a
{
background: rgba(70,70,60,.4) !important;
}
.gssb_m > tbody > tr:hover *
{
color: rgba(255,255,255,1) !important;
}

/* hide regular logo */
#hplogo
{display: none !important;}

/* replacement logo (bg inserted at top) */
#lga
{
background-image: none !important;
background-position: center 0px !important;
background-repeat: no-repeat !important;
background-size: 275px 95px !important;
opacity: .4 !important;
position: fixed !important;
bottom: 70% !important;
left: 50% !important;
margin-left: -250px !important;
width: 500px !important;
height: 100px !important;
}

/* switch language */
#als
{
display: table !important;
position: fixed !important;
top: 0 !important;
right: 0 !important;
height: 30px !important;
max-height: 30px !important;
/*width: 100% !important;*/
/*background: rgba(145,45,45,.3) !important;*/
/*opacity: .0001 !important;*/
z-index: 999 !important;
}
#als br
{
display: none !important;
height: 0px !important;
}
#als > font{
/*position: absolute !important;*/
display: block !important;
/*height: 30px !important;*/
/*width: 230px !important;*/
/*bottom : 0px !important;
right: 0px !important;*/
color: #bbb !important;
padding: 6px 10px 6px !important;
visibility: hidden !important;
/*background: rgba(0,0,100,.2) !important;*/
}
#als:hover
{
background: rgba(25,25,20,.5) !important;
}
#als:hover font
{
visibility: visible !important;
}
#mngb:hover ~ div #body #als a,
#mngb ~ div #body #als:hover a {
color: #99f !important;
visibility: visible !important;
}


/* search box */
.tsf-p > div#logocont + div { 
position: fixed !important; 
top: 32% !important;
left: 50% !important;
margin-left: -245px !important;
width: 485px !important;
border: 0 !important;
padding: 0 2px !important;
height: 30px !important;
}

.tsf-p > div#logocont + div > table {
border: 1px solid rgba(128,128,128,1) !important;
background: rgba(10,10,8,.75) !important;
}

#gs_tti0 {
height: 28px !important;
padding-top: 4px !important;
}

/* eliminate extra backgrounds and borders */
.tsf-p > div#logocont:not(#foo) + div:not(#foo) > table:not(#foo) *
{
background: rgba(128,128,128,0) !important;
border-color: rgba(128,128,128,0) !important;
}

/* input */
#lst-ib
{color: rgba(200,200,160,1) !important;}
#lst-ib::-moz-selection
{
background-color: rgba(120,120,100,.5) !important;
color: rgba(255,255,255,1) !important;
}
#lst-ib::selection
{
background-color: rgba(120,120,100,.5) !important;
color: rgba(255,255,255,1) !important;
}


/* search button */
.jsb input:first-child { 
position: fixed !important;
opacity: .0001!important;
top: 32% !important;
left: 50% !important;
width: 100px !important;
margin-left: 248px !important;
margin-top: 0px !important;
padding: 1px 8px 2px !important;
height: 27px !important;
border: 1px solid rgba(228,228,228,1) !important;
cursor: pointer !important;
}

/* fabricated search button image */
.tsf-p > div#logocont + div:after
{
content: "Search" !important;
color: rgba(255,255,255,.6) !important;
font-size: 20px !important;
font-family: Carmichael, Papyrus, Viner Hand ITC, Enviro, Comic Sans MS, Comic Sans, sans-serif !important;
position: absolute!important;
top: 0px !important;
padding-top: 1px !important;
padding-left: 0px !important;
width: 113px !important;
bottom: -2px !important;
right: -120px !important;
background: rgba(10,10,5,.75) !important;
z-index: -1 !important;
border: 1px solid rgba(128,128,128,1) !important;
text-align: center !important;
}

/* autosuggest search and lucky buttons */
.gssb_m > tbody > tr .gssb_g input{
color: rgba(200,200,200,.75) !important;
border: none !important;
}
.gssb_m > tbody > tr .gssb_g input:hover{
color: rgba(200,200,200,1) !important;
}

/* display none */
/* chrome ads */
#pmocntr2,
.pmoabs
{ display: none !important;}

/* "sign in" */
body #pocs.sft ~ #mngb #gb_70
{visibility: visible !important;}
body #pocs.sft ~ #mngb:after /* "search settings" */
{
content: 'To use "Google Search Home Page: Pond" userstyle you have to turn off "Google Instant predictions" in "Search Settings"' !important;
color: rgba(255,255,255,1) !important;
font-size: 15px !important;
font-family: Comic Sans, sans-serif !important;
position: fixed !important;
top: 50% !important;
left: 50% !important;
margin-top: -50px !important;
margin-left: -400px !important;
width: 800px !important;
height: 100px !important;
padding: 20px !important;
bottom: -2px !important;
background: rgba(10,10,5,.75) !important;
/*z-index: -1 !important;*/
border: 1px solid rgba(128,128,128,1) !important;
visibility: visible !important;
}
/* search settings */
body  #mngb .gbtc > div
{
display: block !important;
background: rgba(25,25,20,.5) !important;
}
body  #mngb .gbtc > div > div,
body  #mngb .gbtc > div > div > div
{
background: rgba(25,25,20,0) !important;
border: none !important;
}
body  #mngb #gbd5{position: static !important;}
/* search settings link */
.gbkc .gbmt{color: #66c !important;}
.gbkc .gbmt:hover{color: #99f !important;}
#gb_70 .gbgs/* "sign in" */
{
background: rgba(0,0,0,1) !important;
color: rgba(150,150,150,1) !important;
opacity: .75 !important;
}
#gb_70:hover .gbgs/* "sign in" */
{opacity: 1 !important;}

/* white bg on instant header */
body #pocs.sft ~ #mngb #gbx1,
body #pocs.sft ~ #mngb #appbar,
body #pocs.sft ~ #mngb #hdtb 
{
background: rgba(0,0,0,0) !important;
border: none !important;
}

#prm{
display: table !important;
position: fixed !important;
left: 0 !important;
bottom: 0 !important;
z-index: 999 !important;
height: auto !important;
padding: 10px !important;
}
#prm br{
display: none !important;
}
#prm a {
color: #99f !important;
}
#prm font {
color: #ccc !important;
visibility: hidden !important;
font-size: 10pt !important;
}
#prm:hover font {
visibility: visible !important;}


}
