css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(webhallen.com){

#searchResultLayer{
  margin:auto;
  left:33.5%;
}
#site_container{
  margin:auto;
  border:1px solid #b3cbde;
}
#site_content{
  border-right:0px solid #b3cbde !important;
}
body{
  background:#000;
}
div#site_content{
    background:#252525 !important;
}
#big_focus_container_top_left_corner_image img{
   	width:0px !important;
    height:0px !important;
}
div#site_content_middle #site_content_middle_page_start{
    background:#252525 !important;
}
div#site_content_middle_page_start div.small_focus_section_nav_bar_bottom{
    background:#202020 !important;
    border-top: 1px solid #000 !important;
    border-left: 1px solid #000 !important;
    border-right: 1px solid #000 !important;
}
div.small_focus_section_three_focus_container{
    background:#151515 !important;
    border-bottom: 1px solid #000 !important;
    border-left: 1px solid #000 !important;
    border-right: 1px solid #000 !important;
    margin:0px !important;
}
div.small_focus_section_focus_container{
    background:#252525 !important;
    border-left:1px solid #404040 !important;
    width:155px !important;
}
div.first_small_focus{
	border:0px !important;
}
div.small_focus_section_nav_bar_title{
    color:#ddd !important;
    font-size:12px !important;
    font-weight:normal !important;
}
div#big_ad{
    height:0px !important;
    width:0px !important;
}
.button_s16 div {
    background-image:none !important;
    background: #ddd !important;
    border-radius: 5px !important;
    -webkit-transition: 0.5s !important;
    -moz-transition: 0.5s !important;
    -o-transition: 0.5s !important;
    transition: 0.5s !important;
}
.button_s16 div:hover {
    background: #909090 !important;
}
a.small_focus_right_button{
    background: url(http://webhallen.com/images/start/buttons-indicators.png) -34px;
}
a.small_focus_left_button{
    background: url(http://webhallen.com/images/start/buttons-indicators.png) -0px;
}
div#site_content_middle #site_content_middle_footer_bottom {
  background: #252525 !important;
    color:#ddd !important
}
}
