@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("mail.google.com") {

/*BACKGROUND*/
body {background: #253960 url("data:image/png;base64,") top left repeat-x !important;
	color: #fff !important;}
	
/*LOADING SCREEN*/
#loading {background: #253960 url("data:image/png;base64,") top left repeat-x !important;
	color: #fff !important;}	

/* DEFAULTS FOR ALL LINKS */
a:link, a:visited, .link { color: #82BDFF !important; }
a:active, .link:active { color: #92CDFF !important; }

/* CHANGE GLOBAL FONT */
*{font-family: "Lucida Grande", Tahoma, Verdana !important; font-size:8pt !important;}

/* HIDE ICONS */
.aBJ8he,.gUlKtd { display: none !important;}

/* SOME ENHANCEMENTS FOR THE INPUT FIELD */
input {padding: .2em .4em !important;}
input:not([type="submit"]),
select {background: #ffffff !important;
	border: 1px solid #99F !important;
	color: #300 !important;}

/* SHOW THE USEFUL LINKS (NEW WINDOW, PRINT ALL, ETC.) IN A ROW */
table[class="PwUwPb XoqCub MMcQxe"] td[class="eWTfhb"]>div[class="XoqCub"] {
	width: 98% !important;
	position: absolute !important;
	text-align: center !important;
	right: 0 !important;
}
table[class="PwUwPb XoqCub MMcQxe"] td[class="eWTfhb"]>div[class="XoqCub"] div {
	display: inline !important;
}
table[class="PwUwPb XoqCub MMcQxe"] td[class="eWTfhb"]>div[class="XoqCub"] span img {
	margin-left: 1em !important;
}

/* BLOCK THE ADS TABLE IN THE MESSAGE WINDOW */
td[class="eWTfhb"]>div[class="XoqCub"]>div[class="XoqCub"]:last-child {display: none !important;}

/* ENLARGE MENU BAR FONT */
.zD5BAe,.HSZged {font-size: 12px !important;}

/* FONTS COLOR TO WHITE */
.nQ6QTe,.oi23Hf,.bSwwie,.l73JSe,.zD5BAe,.bsABdf,.cBhtOe,.SUicbd,.DiWSpb,.VfONdd,.bKmyId,.DiWSpb,.vZFPPc,.z8N7nd,.vwcZUe,.wTQwoc,.AiCerc,.msgb,.u7uAnb {color: #FFFFFF !important;}

/* FONTS COLOR TO  BLUE */
.qZkfSe,.SvrlRe,.m7Qktd,.haNaWc,.IUntof,.GaVz0,.eGbrz .l73JSe,.rj1J6b {color: #82BDFF !important;}

/* FONTS COLOR TO  BLACK */
.MRoIub,.mrKIf,.f8w49d {color: #000000 !important;}

/* MOVE THE MAIL BODY DOWN, SO THAT THE USEFUL LINKS WON'T COVER ON IT */
td[class="eWTfhb"]>div[class="XoqCub ice3Ad"] {margin-top: 1em !important;}

/* WIDEN THE TABLE AFTER WE BLOCKED THE ADS */
table[class*="PwUwPb XoqCub MMcQxe"],
table[class="NQNkZd"],
table[class="N2mZ7b"] {width: 99% !important;}

/* THE REPLY TEXTAREA UNDER THE MAILBODY SHOULD ALSO BE WIDEN */
textarea.gV2Gwc {width: 98.5% !important;}

/* REDESIGN MESSAGE LIST - CHANGE SENDER FONT COLOR TO BLACK */
.qNUdo,.k62PNc {color: #000000 !important;}

/* REDESIGN MESSAGE LIST - CHANGE SUBJECT FONT COLOR TO DARK GRAY */
.HhG5wd {color: #515459 !important;}

/* REDESIGN MESSAGE LIST - CHANGE UNREAD MESSAGE BACKGROUND TO GRAY */
tr[class^="xweT7d QhHSYc"] {background: #D8D8D8 !important;}

/* REDESIGN MESSAGE LIST - CHANGE READ MESSAGE BACKGROUND TO LIGHT GRAY */
tr[class^="xweT7d AnqB9d"] {background: #E8E8E8 !important;}

/* REDESIGN MESSAGE LIST - HOVER ITEM */
tr[class^="xweT7d AnqB9d"]:hover, tr[class^="xweT7d QhHSYc"]:hover {background-color : #92CDFF !important;}

/* REDESIGN MESSAGE LIST - REDUCE FONT SIZE OF EMAIL SNIPPETS */
.bEeVec {font-size:10px !important;}

/* REDESIGN MESSAGE DISPLAY - CHANGE SUBJECT FONT COLOR TO BLUE */
.VrHWId {color: #515459 !important; font-size:11px !important; padding-left:7px !important;}

/* HIDE CHAT/CONTACTS BOX  */
div[class="XoqCub a3hTGd"] { display: none !important; }

/* HIDE INVITES BOX */
div[class="XoqCub I94Sdc"] { display: none !important; }

/* HIDE THE FOOTER BAR */
div.s7hnoe, div.Ax9Ooe, div.tBLpAf, .LaiJff { display: none !important; }

/* LEFT MENU */
.NIPhib .zD5BAe, .gb1 {color:#FFFFFF !important; text-decoration:none !important; font-size:12px !important; }

/* LABEL MENU */
.XPj4ef .R7iiN {background-color:#c3d9ff !important;}

/* ADS FROM EMAILS */
.qWFFaf{display:none!important}

/* NOTIFY MENU */
.BjBjHb, .seWxW {background-color:#cccccc !important;}

/* HIDE SPAM COUNT */
span#ds_spam b,.ACpQre a[href$='#spam']{visibility:hidden;}span#ds_spam b::before,.ACpQre a[href$='#spam']::before{content:'Spam';visibility:visible;font-weight:400;text-decoration:none;}

/* FIX LOAD SCREEN */
.msgb {background: #253960 url("data:image/png;base64,") !important;}

/* METALLIC GOOGLE LOGO */     
.zYsCRb {background-image: url(data:image/png;base64,) !important; background-color: transparent !important; height: 55px !important; margin-top: 0 !important; padding-left: 123px !important; width: 0 !important;}
}
