css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("earbits.com") {
    /* "hack/fix" bug */
    html {
        max-width: 100% !important;
        max-height: 100% !important;
        overflow: hidden !important;
    }
    /* trash */
    #ki_tab,
    #ki_main,
    #feedbackify,
    /* minimalist - better */
    #audio-controls .track-info,
    .btn-like,
    .btn-twitter,
    #footer,
    /* argh Facebook! */
    #header-auth,
    .btn-fb,
    .fb-like,
    /* hide twitter follow */
    .twitter-follow,
    /* stop touching me! */
    .view-popup-mailing-list-join {
        display: none !important;
    }
    
    #audio-controls {
        width: 280px !important;
    }
    
    .track-info-box {
        height: 200px !important;
        background: none !important;
        background-image: -moz-linear-gradient(top, rgba(20, 20, 20, 0), rgba(20, 20, 20, 0.7), rgba(20, 20 , 20, 0.8), rgba(20, 20 , 20, 0.9), rgba(20, 20 , 20, 1)) !important;
    }
    .track-info {
        text-shadow: 0 1px 1px rgba(0, 0, 0, 0.8);
    }
    #player .track-info-box .track-info-box-content .artist-name {
        margin: 10px 0px 3px !important;
    }
    #player .track-info-box .track-info-box-content ul.secondary-teasers {
        position: absolute !important;
        padding-left: 134px !important;
        bottom: 22px;
    }
    
    #current-track {
        bottom: 0px !important;
    }
}
