@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("https://www.tumblr.com/"), url-prefix("http://www.tumblr.com/")

{

#posts .post.new_post 
{
min-height: 88px !important;
}

.new_post_label/*_icon*/ > i
{
background-image: url('http://img.photobucket.com/albums/v420/KaKaVegeGurl/Tumblr%20Dashboard%20Buttons/GoTDashboardClassicColors3_zpse8241d9b.png') !important;
background-size: 513px, 190px !important;
margin-left: 3px !important;
margin-top: -3px !important;
background-repeat: no-repeat !important;
}

.new_post_label
{
padding: 62px 1px 5px 0px !important;
}

.new_post_label i
{
height: 60px !important;
}

.new_post_label > i 
{
color: transparent !important;
}

#new_post_label_text > i
{
background-position: 3px -4px !important;
}
#new_post_label_text:active > i
{
background-position: 3px -4px !important;
}
#new_post_label_text:hover > i
{
background-position: 3px -6px !important;
}

#new_post_label_photo > i
{
background-position: -72px -4px !important;
}
#new_post_label_photo:active > i
{
background-position: -72px -4px !important;
}
#new_post_label_photo:hover > i
{
background-position: -72px -6px !important;
}

