@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(www.lastfm.de),domain(beta.last.fm),domain(last.fm),
domain(lastfm.com.br),domain(lastfm.es),domain(lastfm.fr),domain(lastfm.it),
domain(lastfm.pl),domain(lastfm.se),domain(lastfm.com.tr),domain(lastfm.ru),
domain(lastfm.jp),domain(cn.last.fm)

{





body {
background: #E7D1BC !important;
background: url('http://colourlovers.com.s3.amazonaws.com/images/patterns/2324/2324036.png') center right repeat fixed !important;
text-align:justify !important;
font-family: 'Trebuchet MS' !important;
color: #6F706F !important;}


 /* Header */
#fauxHeader, #lastfmLogo, #primaryNav .nav-link:hover, #profileLinks .divider a,
#loginLink .profile-link, #loginLink .profile-link strong, #idBadgerUser,
#idBadgerDropper, #siteSearchBox, #siteSearchSubmit,
#headerLangToggle img.globe_icon
{background-image:url("http://imageshack.us/a/img153/826/headersprite2.png") !important;
color: white !important;}

#header a {color: white !important;}

a {color:#9F7343 !important; -webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear;}
a:hover {color:#E982A6 !important; text-decoration:none !important; font-weight:bold !important;
-webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear;}

img {-webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear;}
img:hover {opacity:0.6 !important; -webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear;}  


/*#fauxHeader, #fauxHeaderContainer, #header, #headerWrapper {height: 10px !important; }
#fauxHeader:hover, #fauxHeaderContainer:hover {height: 70px !important;}

#lastfmLogo {background:transparent !important;}
#primaryNav a {color: transparent !important;}*/


header {
background: -moz-linear-gradient(bottom, #E7D1BC 0%, #F9F3E8 100%) !important;
background: -webkit-linear-gradient(bottom, #E7D1BC 0%, #F9F3E8B 100%) !important;
background: -o-linear-gradient(bottom, #E7D1BC 0%, #F9F3E8 100%) !important;
border-radius: 30px 30px 0px 0px !important; border: 1px dashed #fff !important;}

header h1 {color: #E982A6 !important; text-transform:uppercase !important; letter-spacing:5px !important;
font-family: 'Cambria'; text-shadow: 0px 0px 3px #E982A6 !important;}

header a {color: #E982A6 !important; text-transform:uppercase !important; font-size:11px !important;
font-family: 'Century Gothic'; text-shadow: 0px 0px 3px #E982A6 !important;}
header a:hover {text-decoration:none !important; font-weight:bold !important;}

#content, article  {background: #F9F3E8 !important; border-radius: 30px 30px 0px 0px !important;
box-shadow: 0px 0px 15px white !important; border: 1px dashed #fff !important;}



article header {padding:10px !important; margin-left:-20px !important; margin-right:-20px !important;}

.artist-wiki header {background: transparent !important; padding-left: 50px !important;
color:#9F7343 !important; text-shadow: 0px 0px 1px #9F7343 !important; letter-spacing:1px !important;
font-family: cambria !important; font-size: 16px !important;}

.heading a, .h2Wrapper {color:#E982A6 !important; text-transform:uppercase !important; font-family: 'Century Gothic';
text-shadow: 0px 0px 3px #E982A6 !important;}



/* Tout ce qui concerne les tableaux */

table.recentTracks td.featured, table.searchResults tr.exactMatch td, table.eventsMedium tr.attending td, table.tracklist td.highlight {
border-top: 1px solid #E7D1BC !important;
background: -moz-linear-gradient(bottom, #E7D1BC 0%, #F9F3E8 100%) !important;
background: -webkit-linear-gradient(bottom, #E7D1BC 0%, #F9F3E8B 100%) !important;
background: -o-linear-gradient(bottom, #E7D1BC 0%, #F9F3E8 100%) !important;}

table td {border-color: #E7D1BC !important;}
table.tracklist tbody tr:hover, table.tracklist tbody tr.open {background: #fff!important;}

div.chartbar span {
background: -moz-linear-gradient(bottom, #E982A6 0%, #F6BCD1 100%) !important;
background: -webkit-linear-gradient(bottom, #E982A6 0%, #F6BCD1 100%) !important;
background: -o-linear-gradient(bottom, E982A6 0%, #F6BCD1 100%) !important;
color: #fff !important;}

table.chart tbody tr td, #libraryList tbody tr td { 
background: #F9F3E8 !important; 
border-top: 1px solid #E7D1BC !important;}

table.chart tbody tr.odd td, #libraryList tbody tr.odd td, .candyStriped tbody tr.odd td, .withHead .topTracks li.odd { 
background: #fff !important; }





.quote {border: 3px solid #E7D1BC !important; border-radius: 10px !important; padding:5px !important;
background: -moz-linear-gradient(bottom, #E7D1BC 0%, #F9F3E8 100%) !important;
background: -webkit-linear-gradient(bottom, #E7D1BC 0%, #F9F3E8B 100%) !important;
background: -o-linear-gradient(bottom, #E7D1BC 0%, #F9F3E8 100%) !important;}
.quote img {max-width:100% !important;}

.messageWrapper, span.messageWrapper {background: #E7D1BC !important; padding:5px !important;}

section.global-tags li a, .btn {background-color: #E982A6 !important; color: #fff !important;} 
section.global-tags button {background-color: #E982A6 !important; color: #fff !important;}
section.personal-tags li a {background-color: #E982A6 !important; color: #fff !important;} 
section.personal-tags button {background-color: #E982A6 !important; color: #fff !important;} 
section.no-tags button {background-color: #E982A6 !important; color: #fff !important;}
section.global-tags li a:hover, .btn:hover {background-color: #D16088 !important; color: #fff !important;} 
section.global-tags button:hover {background-color: #D16088 !important; color: #fff !important;}
section.personal-tags li a:hover {background-color: #D16088 !important; color: #fff !important;} 
section.personal-tags button:hover {background-color: #D16088 !important; color: #fff !important;} 
section.no-tags button:hover {background-color: #D16088 !important; color: #fff !important;}

section, .tags, .r, .g8, .global-tags, .artist-wiki, .artist-top-tracks, section.artist-listeners section, .home-group, .dropdown-btn-menu 
{background: #F9F3E8 !important;}




/* Partie droite d'une page artiste */

.suggestcorrection, section .listener-details {
background: #fff !important; padding: 10px !important; border-radius: 15px !important; 
text-align:center !important; box-shadow: 0px 0px 3px #E982A6 !important;}

.g4 h2 {color: #E982A6 !important;}





/* Partie commentaires */

.author {background: -moz-linear-gradient(bottom, #E7D1BC 0%, #F9F3E8 100%) !important;
background: -webkit-linear-gradient(bottom, #E7D1BC 0%, #F9F3E8B 100%) !important;
background: -o-linear-gradient(bottom, #E7D1BC 0%, #F9F3E8 100%) !important;
border-radius:10px !important;font-size:11px !important;border: #f2e9e2 !important;
padding: 5px !important; text-transform:uppercase !important;letter-spacing:2px !important;
font-family: 'century gothic' !important;}

.message p {font-size: 12px !important; color: #6F706F !important;}



/* Footer */

footer {background: -moz-linear-gradient(bottom, #F9F3E8 0%, #F9F3E8 100%) !important;
background: -webkit-linear-gradient(bottom, #F9F3E8 0%, #F9F3E8B 100%) !important;
background: -o-linear-gradient(bottom, #F9F3E8 0%, #F9F3E8 100%) !important;
text-shadow: 0px 0px 0px #fff !important;}

footer h2 {color: #E982A6 !important;}

.g3 {background: transparent !important;}




/* Librairie */

.wrapper { background: transparent !important;}
.libraryItems  {background: transparent !important; text-align:center !important; margin-left:5.5% !important;}
.page, #pages {background: transparent !important;}
.name {color: #E982A6 !important;}
.plays {font-style:italic !important;}
#libraryNavigation a {background: #fff !important; border-radius: 10px 10px 0px 0px !important;
border: 1px solid #eee !important;}



