css:
@-moz-document domain(pogovorisomnoi.ru){

hr{
    height: 0px !important;
    border: none !important;
    border-bottom: 0px solid #000 !important;
}

body {
    background: #444 url(http://cs320817.vk.me/v320817363/9cc/Kq4pviQwQ5k.jpg) center fixed !important; 
    background-size: cover !important; background-repeat: no-repeat !important;
}

a {
        color: #999 !important;
	font-size: 14px !important;
	font-family: Georgia !important;
}

div.message.system {
    font-family: Ubuntu, Segoe UI Semibold, Segoe UI, Calibri !important;
    text-shadow: 0 0 15px #333 !important;
    font-size: 28px !important;
    color: #999 !important;
    font-style: normal !important;
}

#online {
    color: #999 !important;
}

#chat-states {
    font-family: Ubuntu, Segoe UI Semibold, Segoe UI, Calibri !important;
    text-shadow: 0 0 15px #333 !important;
    font-size: 28px !important;
    color: #999 !important;
}

#conversation-log {
    font-family: Ubuntu, Segoe UI Semibold, Segoe UI, Calibri !important;
    text-shadow: 0 0 15px #333 !important;
    font-size: 28px !important;
    color: #CCC !important;
}

#conversation-log .message.mine {
    color: #EEE !important;
    text-shadow: 0 0 20px #222 !important;
}

#conversation-log .message.system {
    color: #999 !important;
    font-style: normal !important;
}

#stop-conversation {
    font-family: Ubuntu, Segoe UI Semibold, Segoe UI, Calibri !important;
    font-style: normal !important;
    -webkit-border-radius: 0 !important;
    -moz-border-radius: 0 !important;
    border-radius: 0 !important;
    margin-top: -20px !important;
}

button.btn {
    font-size: 13px !important;
    font-family: Ubuntu, Segoe UI Semibold, Segoe UI, Calibri !important;
    font-style: normal !important;
    -webkit-border-radius: 0 !important;
    -moz-border-radius: 0 !important;
    border-radius: 0 !important;
}

button.btn.btn-large {
    padding: 14px 19px !important;
}

#start-chat {
    font-family: Ubuntu, Segoe UI Semibold, Segoe UI, Calibri !important;
    font-style: normal !important;
    -webkit-border-radius: 0 !important;
    -moz-border-radius: 0 !important;
    border-radius: 0 !important;
}

#chat-form-message {
    font-family: Ubuntu, Segoe UI Semibold, Segoe UI, Calibri !important;
    box-shadow: inset 0 0 30px rgba(0, 0, 0, 0.4) !important;
    background: rgba(0, 0, 0, 0) !important;
    font-size: 22px !important;
    color: #EEE !important;
    -webkit-border-radius: 0 !important;
    -moz-border-radius: 0 !important;
    border-radius: 0 !important;
    height: 50px !important;
}

#banrequest {
    font-family: Ubuntu, Segoe UI Semibold, Segoe UI, Calibri !important;
    font-style: normal !important;
    -webkit-border-radius: 0 !important;
    -moz-border-radius: 0 !important;
    border-radius: 0 !important;
}

#logo {
    font-family: Ubuntu, Segoe UI Semibold, Segoe UI, Calibri !important;
    font-size: 35px !important;
    color: #EEE !important;
    text-shadow: 0 0 20px #222 !important;
}

}
