css:
@-moz-document domain("ru.tankiforum.com") {

/* white ru.tankiforum.com */

a {
  color:#1560BD !important;
}

a:hover {
  text-decoration: underline !important;
}

.admin {
  color: red !important;
  font-size: 11pt !important;
}

.Moderator {
  color: green !important;
  font-size: 11pt !important;
}

body, div, td, th, .cattitle, blockquote, select, .liteoption { 
  color: black !important;
  background: none !important; 
  background-color: ghostwhite !important; 
  font-size: 10pt !important;
}

.post_block h3, p.citation, #community_app_menu > li.active > a, .ipsFilterbar li.active a, .ipsFilterbar.bar.altbar li.active a, .topic_buttons li.important a, .topic_buttons li.important span, .ipsButton .important, .topic_buttons li a, .topic_buttons li span, .ipsButton, .maintitle, #search_options, .unread {
  color: black !important;
  background: none !important;
  background-color: lightgray !important; 
}

#search_wrap {
  color: black !important;
  background: none !important;
  background-color: ghostwhite !important; 
}

.liteoption:hover {
  background-color: green !important;
}

.pagination a:hover, .pagination a:active {
  background-color: #68a527 !important;
  color: #FFF !important;
}

span[style="background-color: rgb(36, 33, 30)"], span[style="background-color: rgb(52, 49, 45)"], span[style="background-color: rgb(59, 54, 49)"] {
  background-color: ghostwhite !important; 
}

}
