/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("play.pokemonshowdown.com") {

                                          
div#simheader {background-color: #1C1C1C !important;} 
div#simheader {border-bottom-color: black !important;}      
                    
div.battle-log {background-color: #121212 !important;}
div.battle-log {background-image: none !important;}
div.battle-log {border-left-color: black !important;}
div.battle-log em {color: silver !important;}
div.battle-log a {color: #7653D7 !important;}

div.mainsection.ladder {background-color: #1C1C1C !important;}
