div.mainsection.ladder th {background: none !important;}

div.mainsection.teamlist {background-color: #1c1c1c !important;}
div.mainsection.teamlist {width: 640px !important;}
div.mainsection.teamlist p {color: silver !important;}



div.battle-log-add {background-color: #1C1C1C !important;}
div.battle-log-add {border-top-color: black !important;}
div.battle-log-add {border-left-color: black !important;}


textarea.textbox {background-color: #303030 !important;}
textarea.textbox {box-shadow: none !important;}
textarea.textbox {border-color: black !important;}


ul.userlist {background-color: #1e1e1e !important;}
ul.userlist {border-color: black !important;}
.userlist li button:hover {background-color: #3A3A3A !important;}
.userlist li button:active {background-color: #121212 !important;}
.userlist li.cur button, .userlist li.cur button:hover {background: black !important;}
.userlist li.cur button, .userlist li.cur button:hover {border-color: #626262 !important;}
.userlist li.cur button, .userlist li.cur button:active {margin: 0px 0px 0px 0px !important;}
.userlist li {border-color: black !important;}
.userlist li.cur button, .userlist li.cur button:active {background-color: #121212 !important;}
.userlist li.cur button, .userlist li.cur button:active {border-left: 0px !important;}


div.maintop {background-color: #121212 !important;}
div.maintop label.label {color: silver !important;}
div.maintop span#lobby-teamselect {color: silver !important;}
h3 {background-color: #1e1e1e !important;}
h3 {border-color: black !important;}
h3 {box-shadow: none !important;}

.teambuilder-results h3, .teambuilder-results .utilichart h3, .roomlist h3, .mainpopup h3 {color: silver !important;}
.teambuilder-results h3, .teambuilder-results .utilichart h3, .roomlist h3, .mainpopup h3 {text-shadow: none !important;}


div#lobby-roomlist.roomlist {background-color: #121212 !important;}
div#lobby-roomlist.roomlist a {background-color: #1C1C1C !important;}
div#lobby-roomlist.roomlist a {border-color: black !important;}
div#lobby-roomlist.roomlist a {box-shadow: none !important;}
div#lobby-roomlist.roomlist small {color: silver !important;}
div#lobby-roomlist.roomlist small {text-shadow: none !important;}
div#lobby-roomlist.roomlist em.p1 {text-shadow: none !important;}
div#lobby-roomlist.roomlist em.p1 {color: #357EC7 !important;}
div#lobby-roomlist.roomlist em.p2 {text-shadow: none !important;}
div#lobby-roomlist.roomlist em.p2 {color: #7E3517 !important;}
div#lobby-roomlist.roomlist a:hover {background-color: #3A3A3A !important;}

div.roomlist a {background-color: #1C1C1C !important;}
div.roomlist a {border-color: black !important;}
div.roomlist a {box-shadow: none !important;}
div.roomlist small {text-shadow: none !important;}
div.roomlist small {color: silver !important;}
div.roomlist em.p1 {text-shadow: none !important;}
div.roomlist em.p1 {color: #357EC7 !important;}
div.roomlist em.p2 {text-shadow: none !important;}
div.roomlist em.p2 {color: #7E3517 !important;}
div.roomlist a:hover {background-color: #3A3A3A !important;}

div#leftbar {background-color: #151515 !important;}
div.maintop {border-left:1px solid black !important;}
div#leftbarbg {display: none !important}


a#tabtab-lobby {background-color: #1C1C1C !important;}
a#tabtab-lobby {border-color: black !important;}
a#tabtab-lobby {box-shadow: none !important;}
i.icon-comments-alt {text-shadow: none !important;}
i.icon-comments-alt {color: white !important;}
a#tabtab-lobby:hover {background-color: #3A3A3A !important;}
a#tabtab-lobby.tab.cur {background-color: #121212 !important;}



#leftbar a {color: silver !important;}
#leftbar a {text-shadow: none !important;}

a#tabtab-teambuilder {background-color: #1C1C1C !important;}
a#tabtab-teambuilder {border-color: black !important;}
a#tabtab-teambuilder {box-shadow: none !important;}
i.icon-edit {color: white !important;}
a#tabtab-teambuilder:hover {background-color: #3A3A3A !important;}
a#tabtab-teambuilder.cur {background-color: #121212 !important;}


a#tabtab-ladder {background-color: #1C1C1C !important;}
a#tabtab-ladder {border-color: black !important;}
a#tabtab-ladder {box-shadow: none !important;}
a#tabtab-ladder:hover {background-color: #3A3A3A !important;}
i.icon-list-ol {color: white !important;}
a#tabtab-ladder.cur {background-color: #121212 !important;}


div.action-form {border-color: black !important;}
div.action-form {background: #1C1C1C !important;}
div.action-form strong {color: silver !important;}
div.action-form small {color: silver !important;}
div.action-form button strong {color: black !important;}
div.action-form {color: silver !important;}


div.teamchartbox {border-top-color: black !important;}

div.setchart {background-color: #1C1C1C !important;}
div.setchart {border-color: #544E4F !important;}
div.setchart {border-width: 1px !important;}
div.setchart {box-shadow: none !important;}
div.setchart {background: none !important;}
div.setchart label {text-shadow: none !important;}
div.setchart label {color: silver !important;}

input.chartinput {background-color: #1C1C1C !important;}
input.chartinput {box-shadow: none !important;}
input.chartinput {border-color: #544E4F !important;}
input.chartinput {color: silver !important;}
